//JSilde的函数原型
function JSlide(container, config) {
    var self = this,
        _config = {
            // 当前轮播显示的第一个item的index
            // positionIndex: 0,
            // 当前高亮的轮播item
            // activeIndex: 0,
            // 轮播时间间隔
            slideInterval: 5000,
            // 轮播滚动持续事件
            slideDuration: 500,
            // 轮播图滚动效果 swing/linear
            slideEasing: 'swing',
            // slideEasing: 'linear',
            // 是否自动轮播
            autoSlide: true,
            // 轮播item mouseover时是否停止自动轮播
            isMouseoverStop: true,
            steps: 1
        };
    if(!container) {return false;}

    self.container = $(container);
    self.timer = null;
    self.config = $.extend(_config, config);

    self._init();
}

JSlide.prototype = {            
    //创建select结构
    _init: function() {                 
        var self = this;
        // 解析html结构
        self._parseMarkup();
        // 初始化滚动位置
        self.positionIndex = 0;
        // 初始化设置滚动位置
        self.direction = FORWARD;
        // 获取当前高亮item和滚动位置
        self._getActive();
        // 设置当前高亮item和滚动位置
        self._setActive();
        // 初始化滚动
        self._initSlide();
        // 绑定事件
        self._bind();
    },
    //解析html结构
    _parseMarkup: function() {
        var self = this,
            cfg = self.config,
            container = self.container,
            items = container.find(cfg.items);
        // 每个滚动item的width
        self.stepWidth = items.eq(0).outerWidth(true);
        self.len = items.length;

        if(container.find(cfg.content).length) {

            items.parent().css('position', 'relative');

            // 轮播item设置index
            items.each(function(i, item) {
                item = $(item);
                item.attr('data-slide-idx', i);
            });
        }
    },
    //事件绑定
    _bind: function() {
        var self = this,
            cfg = self.config,
            container = self.container;

        if(container.find(cfg.content).length) {
            self._bindContent();
        }
        self._bindTrigger();
    },
    //轮播区域 事件绑定
    _bindContent: function() {
        var self = this,
            cfg = self.config,
            container = self.container,
            imgs = container.find(cfg.itemImgs);

        // by wanchuan 点击专题时，将点击专题的编号、当前左边显示的第一个专题的编号、点击专题的链接地址添加到本地存储里。
        container.delegate(cfg.items, 'click', function(ev) {
            // console.log(ev.currentTarget);
            var target = $(ev.currentTarget);
                ls = {};
            self.activeIndex = target.attr('data-slide-idx');
            self.positionIndex = self.positionIndex;
            self.href = target.find('a').attr('href');
            self.__setActive();
        });

        if(!cfg.isMouseoverStop) {return true;} //如果条件为真（这里取反了参数，参数传的是真，取反了就为假，条件应该不成立？），则继续执行

        container.delegate(cfg.items, 'mouseenter mouseleave', function(ev) {
            // console.log('items mouse event:', ev.target, ev.type);
            // 鼠标移到、移出轮播图item时 
            var type = ev.type;
            if(type === 'mouseenter') {
                self._clearTimer();
            }else {
                self._initSlide();
            }
        });
    },
    //轮播trigger 事件绑定
    _bindTrigger: function() {
        var self = this,
            cfg = self.config,
            container = self.container;

        container.delegate(cfg.prevTrigger, 'click', function(ev) {
            var target = $(ev.currentTarget),
                idx = self.positionIndex - cfg.steps;
            if(target.hasClass(cfg.prevTriggerDisableCls)) { return false;}

            self.direction = BACKWARD;

            if(idx < 0) {
                idx = 0;
            }
            self._slideTo(idx);
        });

        container.delegate(cfg.nextTrigger, 'click', function(ev) {
            var target = $(ev.currentTarget), 
                steps = cfg.steps,
                idx = self.positionIndex + steps,
                len = self.len;
            if(target.hasClass(cfg.nextTriggerDisableCls)) { return false;}

            self.direction = FORWARD;

            if(idx + steps > len) {
                idx = len - steps;
            }
            self._slideTo(idx);
        });

        container.delegate(cfg.prevTrigger, 'mouseenter mouseleave', function(ev) {
            var type = ev.type,
                target = $(ev.currentTarget);

            if(type === 'mouseenter') {
                target.removeClass(cfg.prevTriggerOutCls);
                target.addClass(cfg.prevTriggerOverCLs);
            }else {
                target.removeClass(cfg.prevTriggerOverCLs);
                target.addClass(cfg.prevTriggerOutCls);
            }
        });

        container.delegate(cfg.nextTrigger, 'mouseenter mouseleave', function(ev) {
            var type = ev.type,
                target = $(ev.currentTarget);

            if(type === 'mouseenter') {
                target.removeClass(cfg.nextTriggerOutCls);
                target.addClass(cfg.nextTriggerOverCls);
            }else {
                target.removeClass(cfg.nextTriggerOverCls);
                target.addClass(cfg.nextTriggerOutCls);
            }
        });
    },
    //设置滚动
    _initSlide: function() {
        var self = this,
            cfg = self.config,
            container = self.container;

        if(!cfg.autoSlide) {return false;}

        self.timer = setInterval(function() {
            var index = self.positionIndex,
                lastIdx = self.len - 1;

            if(self.direction === BACKWARD) {
                index -= 7;
                if(index < 0) {
                    index = 0;
                }
            }else {
                index += 7;
                if(index + 7 > lastIdx) {
                    index = lastIdx - 6;
                }
            }

            self._slideTo(index);
        }, cfg.slideInterval);
    },
    ///滚动
    //@param index {Number} 滚动到的序号
    _slideTo: function(index) {
        var self = this,
            cfg = self.config,
            container = self.container,
            content = container.find(cfg.content),
            positionIndex = self.positionIndex,
            left,
            slideContent = container.find(cfg.items).parent();
        slideContent.stop(true, true);

        left = slideContent.css('left');
        if(left === 'auto') {
            left = 0;
        }else {
            left = parseInt(left, 10);
        }
        distance = index * self.stepWidth + left;
        // console.log('slide to:', index, left, distance);

        $(self).trigger('slideStart', [self.positionIndex, index]);

        self._dealTrigger(index);
        slideContent.animate({"left": "-=" + distance +"px"}, cfg.slideDuration, cfg.slideEasing, function() {

            $(self).trigger('slideEnd', [self.positionIndex, index]);
            // self.__setActive();
        });
    },
    //处理向前、向后按钮状态
    //@param index {Number} 滚动的位置
    _dealTrigger: function(index) {
        var self = this,
            cfg = self.config,
            container = self.container,
            prevTrigger = container.find(cfg.prevTrigger),
            prevTriggerDisableCls = cfg.prevTriggerDisableCls,
            nextTrigger = container.find(cfg.nextTrigger),
            nextTriggerDisableCls = cfg.nextTriggerDisableCls;

        self.positionIndex = index;

        prevTrigger.removeClass(prevTriggerDisableCls);
        nextTrigger.removeClass(nextTriggerDisableCls);

        if(index === 0) {
            prevTrigger.addClass(prevTriggerDisableCls);
            self.direction = FORWARD;
        }else if(index + cfg.steps === self.len) {
            nextTrigger.addClass(nextTriggerDisableCls);
            self.direction = BACKWARD;  
        }
    },      
    //获取当前高亮item和滚动位置
    _getActive: function() {
        var self = this,
            cfg = self.config,
            href = window.location.href,
            items = self.container.find(cfg.items),
            len = self.len,
            steps = cfg.steps,
            ls;
        try{
            ls = $.parseJSON(localStorage.getItem(cfg.uid)) || {};
        }catch(e) {
            ls = {};
        }
        if(href === ls.href) {
            self.activeIndex = ls.activeIndex;
            self.positionIndex = ls.positionIndex;
        }else {
            items.each(function(i, item) {
                if($(item).find('a').attr('href') === href) {
                    self.activeIndex = i;

                    self.positionIndex = i;
                    if(i + steps > len - 1) {
                        self.positionIndex = len - steps;
                    }
                }
            });
        }


        self.href = href;
        self.__setActive();

        return self;
    },      
    //设置当前高亮item和滚动位置
    _setActive: function() {
        var self = this,
            cfg = self.config,
            container = self.container,
            activeCls = cfg.activeCls,
            items = container.find(cfg.items),
            idx = self.activeIndex;

        items.removeClass(activeCls);

        if(idx >= 0) {
            items.eq(idx).addClass(activeCls);
        }

        self._slideTo(self.positionIndex);
    },          
    //本地存储数据
    __setActive: function() {   
        var self = this,
            ls = [];
        // console.log(JSON.stringify(ls));
        // localStorage.setItem(self.config.uid, JSON.stringify(ls));
        ls.push('{"activeIndex":');
        ls.push(self.activeIndex + ',');    
        ls.push('"positionIndex":');
        ls.push(self.positionIndex + ',');  
        ls.push('"href":');
        ls.push('"' + self.href + '"}');
        // console.log(ls.join(''));
        localStorage.setItem(self.config.uid, ls.join(''));
    },          
    //清空timer
    _clearTimer: function() {
        var self = this;
        if(self.timer) {
            clearInterval(self.timer);
            self.timer = null;
        }
    }
}

baseSlide:function(args){
			if(args == undefined){
				if(validateData($(this).attr("module-param"))){
					var args = eval('('+$(this).attr("module-param")+')');
				}
			}
			if(!$(this)[0]) return;

			// 定义传入的CSS调用变量
			var _this=this,
				param=$.extend({imgArea:".jbannerImg", imgNode:".jbannerImg dl", tabArea:".jbannerTab", tabNode:".jbannerTab span", photoName:".jDesc", arrowLeft:".jPreOut", arrowRight:".jNextOut", arrowLeftOver:"jPreOver", arrowRightOver:"jNextOver", defaultClass:"show", message:".jMessageRemind", pageMode:".j-edit-page", slideDirection:"left", timer:"3"}, args),
				imgArea = $(_this).find(param.imgArea),
				imgNode = $(_this).find(param.imgNode),
				tabArea = $(_this).find(param.tabArea),
				tabNode = $(_this).find(param.tabNode),
				photoName = $(_this).find(param.photoName),
				arrowLeft = $(_this).find(param.arrowLeft),
				arrowRight = $(_this).find(param.arrowRight),
				arrowLeftOver = param.arrowLeftOver,
				arrowRightOver = param.arrowRightOver,
				defaultClass = param.defaultClass,
				message = $(_this).find(param.message),
				pageMode = $(param.pageMode),
				slideDirection = param.slideDirection,
				timer = param.timer*1000,
				scroll;


			jshop.module.ridLazy(_this);
			scroll = (slideDirection == "top")?"scrollTop":"scrollLeft";

			// 当处于装修页面，同时获取到的图像宽度和布局宽度不同时显示提示消息
			if(imgArea.find("img").width() != $(_this).width()){
				if(pageMode[0]){jshop.module.Banner.errorMessage.call(this, message);}
			}

			// 初始化显示第一个
			imgNode.eq(0).addClass(defaultClass);
			tabNode.eq(0).addClass(defaultClass);
			photoName.text(imgNode.eq(0).find("img").attr("title"));

			// 初始化部分动态css属性
			var imgHeight = imgNode.find("img").height();
			$(_this).css({height:imgHeight});
			imgArea.css({width:$(_this).parent().width(), height:$(_this).parent().height()});
			if(slideDirection == "top"){
				imgArea.children().css({height:10000, width:"auto"});
				imgNode.css({width:$(_this).parent().width(),height:"auto","float":"none"});
			}else{
				imgArea.children().css({width:10000, height:"auto"});
				imgNode.css({width:$(_this).parent().width(),height:"100%","float":"left"});
			};//将这个宽度写在css里，在ie6下面，获取到的父级宽度是被这个元素撑开的宽度

			imgNode.find("img").css({height:imgHeight,width:"100%"});//img的宽度写在css里，在ie6/7下面图片的高度会自动跟随宽度变化而变形

			// 定义局部变量
			var	index = 0,
				moveRange,
				partTime,
				direction = 1;
			if(slideDirection == "top"){moveRange = imgNode.height()}else{moveRange = imgNode.width()}
			imgArea[0][scroll] = index * moveRange;

			// 默认循环显示图片
			var time = setInterval(imgMove, timer);

			// 给每个tab缩略图绑定事件
			tabNode.each(function(i,elem){
				$(this).click(function(){
					imgNode.eq(index).removeClass(defaultClass);
					tabNode.eq(index).removeClass(defaultClass);
					index = i;
					imgNode.eq(index).addClass(defaultClass);
					tabNode.eq(index).addClass(defaultClass);
					photoName.text(imgNode.eq(index).find("img").attr("title"));
					allImgMove();
				});
			});

			// 每一张图片和所有图片
			function allImgMove(){
				clearInterval(partTime);
				clearInterval(time);
				partTime = setInterval(oneImgMove, 30);
				time = setInterval(imgMove, timer);
			}

			// 每一张图片分10次移动
			function oneImgMove(){
				var nowMoveRange = (index * moveRange) - imgArea[0][scroll],
					partImgRange = nowMoveRange > 0 ? Math.ceil(nowMoveRange / 10) : Math.floor(nowMoveRange / 10);
					imgArea[0][scroll] += partImgRange;
				if (partImgRange == 0){
					clearInterval(partTime);
					imgNode.eq(index).addClass(defaultClass);
					tabNode.eq(index).addClass(defaultClass);
					photoName.text(imgNode.eq(index).find("img").attr("title"));
					partImgRange = null;
				}
			}

			// 所有图片移动
			function imgMove(){
				if (direction == 1){
					if (index < imgNode.length - 1){
						classOper([imgNode,tabNode],defaultClass,true);
					}else{
						direction = 0;
						classOper([imgNode,tabNode],defaultClass,false);
					}
				}else{
					if (index > 0){
						classOper([imgNode,tabNode],defaultClass,false);
					}else{
						direction = 1;
						classOper([imgNode,tabNode],defaultClass,true);
					}
				}
				photoName.text(imgNode.eq(index).find("img").attr("title"));
				allImgMove();
			}

			// 节点css类名操作
			function classOper(arr,className,flag){
				arr.each(function(ind,n){
					n.eq(index).removeClass(className);
				})
				flag?(index++):(index--);
				arr.each(function(ind,n){
					n.eq(index).addClass(className);
				});
			}

			// 上一张图片
			arrowLeft.bind({
				click:function(){
					// 判断当前是不是第一张
					if(index != 0){
						classOper([imgNode,tabNode],defaultClass,false);
						allImgMove();
					}
				},
				mouseover:function(){$(this).addClass(arrowLeftOver);},
				mouseout:function(){$(this).removeClass(arrowLeftOver);}
			});

			// 下一张图片
			arrowRight.bind({
				click:function(){
					// 判断当前是不是最后一张
					if(index < imgNode.length - 1){
						classOper([imgNode,tabNode],defaultClass,true);
						allImgMove();
					}
				},
				mouseover:function(){$(this).addClass(arrowRightOver);},
				mouseout:function(){$(this).removeClass(arrowRightOver);}
			});
		}

c_slide: function(args){
	var param=$.extend({node:'li',slideNext:'.btn_next',slidePrev:'.btn_prev',slideType:2,height:360,width:700,timer:2,slideShow:1}, args||{}),
        _this=$(this),
        _currentSlide = 0,
        imgArea = $(_this).find(param.node),
        FrameHeight = param.height,
        FrameWidth = param.width,
        slideNext = $(_this).find(param.slideNext),
        slidePrev = $(_this).find(param.slidePrev),
        slideLength = imgArea.length,
        scrollType = 'left',    //方式（目前只有一种）
        slideType = param.slideType,
        slideShow = param.slideShow,
        _duration = parseInt(parseFloat(param.timer || 0.5)*1000),
        flag = true;
    if(!imgArea.length) return;

    if(slideShow==1){
        slidePrev.hide();
        slideNext.hide();
    }
	//设置宽、高
	_this.css({'height':FrameHeight});
	_this.find('.banner_left').css({'height':FrameHeight,'width':(_this.width()-FrameWidth)/2});
	_this.find('.banner_right').css({'height':FrameHeight,'width':(_this.width()-FrameWidth)/2,'left':parseInt(FrameWidth)+parseInt((_this.width()-FrameWidth)/2)});

	imgArea.css({'height':FrameHeight,'width':FrameWidth});
	imgArea.find('img').css({'height':FrameHeight,'width':FrameWidth});
	_this.find('.u_bannerpic').css({'height':FrameHeight,'width':_this.width()});
	_this.find('ul').css({'left':-(FrameWidth-((_this.width()-FrameWidth)/2))});
	slideNext.css({'top':(FrameHeight-slidePrev.height())/2,'right':((_this.width()-FrameWidth)/2)+20});
	slidePrev.css({'top':(FrameHeight-slidePrev.height())/2,'left':((_this.width()-FrameWidth)/2)+20});
	//克隆第一张和最后一张
	imgArea.eq(slideLength-1).after(imgArea.eq(0).clone());
	imgArea.eq(0).before(imgArea.eq(slideLength-1).clone());

    //给“上一个”、“下一个”按钮添加动作
    function _event(){
        slideNext.bind({
            click:function(){
                if (_currentSlide < slideLength - 1) {
                     _currentSlide += 1;
                     _go(_currentSlide);
                 } else {
                    flag=false;
                 }

            }
        });
        slidePrev.bind({
            click:function(){
                if (_currentSlide > 0) {
                    _currentSlide -= 1;
                    _go(_currentSlide);
                } else {
                    flag=false;
                }
            }
        });
        //定论鼠标划过时，自动轮换的处理
        _this.bind('mouseover', function () {
            slidePrev.show();
            slideNext.show();
            clearTimeout(autoExt);
        });

        _this.bind('mouseout', function () {
            if(slideShow==1){
                slidePrev.hide();
                slideNext.hide();
            }
            clearTimeout(autoExt);
            autoExt = setTimeout(function () {
                extInterval();
            },_duration);
        });
    }

    function _go(index){
        _currentSlide = index;
        if (scrollType == "left") {
            $(_this).find('ul').animate({
                marginLeft: (-index * FrameWidth) + "px"
            }, { duration: 600, queue: false });
        }
        imgArea.removeClass("visited");
        imgArea.eq(index).addClass("visited");
    }

    function extInterval(){
        if(slideType==1){
            if (_currentSlide < slideLength - 1) {
                _currentSlide += 1;
                _go(_currentSlide);
            } else {
                _currentSlide = 0;
                _go(0);
            }
        }else if (slideType==2){
            if(flag){
                if((_currentSlide+1)==(slideLength)){
                    flag=false;
                    _currentSlide -= 1;
                }else{
                    _currentSlide += 1;
                }
            }else{
                flag==false;
                if(_currentSlide==0){
                    flag = true;
                    _currentSlide += 1;
                }else{
                    _currentSlide -= 1;
                }
            }
            _go(_currentSlide);
        }
        var self = this;
        autoExt = setTimeout(function () {
            extInterval();
        }, _duration);
    }
    _event();
    //开始自动轮换
    autoExt = setTimeout(function () {
        extInterval();
    }, _duration);
}

errorMessage:function(message){
			message.show(); setTimeout(function(){message.animate({top:-message.outerHeight(true)},200);},10000);
		}

slide:function(args){
	// 定义传入的CSS调用变量
	var _this=this,
		param=$.extend({imgArea:'.jbannerImg', imgNodeArea:'.jImgNodeArea', imgNode:'.jbannerImg dl', tabArea:'.jbannerTab', tabNode:'.jbannerTab span', photoName:'.jDesc', arrowLeft:'.jPreOut', arrowRight:'.jNextOut', arrowLeftOver:'jPreOver', arrowRightOver:'jNextOver', defaultClass:'show', slideDirection:'left', timer:'3', subFunction:'transparentEffect', eventType:'click', showArrow:1, isCircular:false, isTabAvailable:true, isAreaClick:true, isHoverStop:true}, args),
		imgArea = $(_this).find(param.imgArea),
		imgNode = $(_this).find(param.imgNode),
		tabArea = $(_this).find(param.tabArea),
		tabNode = $(_this).find(param.tabNode),
		photoName = $(_this).find(param.photoName),
		defaultClass = param.defaultClass,
		eventType = param.eventType,
		timer = !param.timer*1000?3000:param.timer*1000,
		scroll,
		imgNodeArea = $(_this).find(param.imgNodeArea),
		isFull = param.isFull;

	//全局变量
	var index = 0,direction = 1,time = null,moveRange = 0,partTime = null,animate = null,enterFlag = false;
	if(!imgNode.length) return;

	//是否鼠标移动到内容区域时，停止轮播
	if(param.isHoverStop){
		imgArea.bind({
			mouseenter:function(){
				enterFlag = true;
				_stop();
			},
			mouseleave:function(){
				enterFlag = false;
				time = setTimeout(imgMove, timer);	
			}
		});
	}
	//轮播图所有效果
	jshop.module.ridLazy(_this);
	var banner = {
		transparentEffect : function(){
			//初始化
			$(_this).css({'background-color':imgNode.eq(index).attr('background')});

			// 调用函数
			init();
			if(param.isTabAvailable){triggerThumbnail();}//如果切换切点可用
			triggerDirection();
			if(param.showArrow!=1){triggerArrow();}
			animate = transparent;
			time = setTimeout(imgMove, timer);
		},
		moveEffect : function(){
			var isTop = (param.slideDirection == 'top')?true:false;
			scroll = (isTop)?"scrollTop":"scrollLeft";

			//初始化
			$(_this).css({'background-color':imgNode.eq(index).attr('background')});
			if(isTop){
				imgNodeArea.css({height:20000, width:$(_this).width()});
				imgNode.css({width:imgNodeArea.width(),height:"auto","float":"none"});
				moveRange = imgNode.height();
				imgArea[0][scroll] = index * moveRange;
			}else{
				imgNodeArea.css({width:20000});
				imgNode.css({width:imgNode.find("img").width(),height:"100%","float":"left"});//将这个宽度写在css里，在ie6下面，获取到的父级宽度是被这个元素撑开的宽度
				moveRange = imgNode.width();
				imgArea[0][scroll] = index * moveRange;
			};

			// 调用函数
			init();
			if(param.isTabAvailable){triggerThumbnail();}//如果切换切点可用
			triggerDirection();	
			if(param.showArrow!=1){triggerArrow();}
			animate = oneImgMove;
			time = setTimeout(imgMove, timer);
		}
	};

	//根据传入的子方法名执行对应的子方法
	if(banner[param.subFunction])
		banner[param.subFunction].call(_this);

	//轮播图初始化
	function init(){
		imgArea.css({width:imgNode.find("img").width(),height:imgNode.find("img").height()});
		imgNode.eq(0).addClass(defaultClass);
		tabNode.eq(0).addClass(defaultClass);
		photoName.text(imgNode.eq(0).find("img").attr("title"));

		if(param.isAreaClick){
			$(_this).click(function(){
				window.open(imgNode.eq(index).attr('ref'));
			});
		}

		autoMiddle();
		$(window).resize(function(){
			autoMiddle();
		});
	}

	// 轮播图自适应居中于屏幕中间
	function autoMiddle(){
		var extra = imgArea.width()-$(_this).width();
		if(extra>0){
			imgArea.css({'margin-left':-extra/2});
		}else{
			imgArea.css('margin','0 auto');
		}
	}

	//给每个tab缩略图绑定事件
	function triggerThumbnail(){
		tabNode.each(function(i,elem){
			$(elem)[eventType](function(){
				imgNode.eq(index).removeClass(defaultClass);
				tabNode.eq(index).removeClass(defaultClass);
				index = i;
				imgNode.eq(index).addClass(defaultClass);
				tabNode.eq(index).addClass(defaultClass);
				photoName.text(imgNode.eq(index).find("img").attr("title"));
				animate();
				return false;
			});
		});
	}

	//点击箭头或数字时，重置时间
	function _stop(){
		clearTimeout(time);
		time = null;
		//clearTimeout(partTime);
		//partTime = null;
		imgNodeArea.clearQueue();
		imgNode.eq(index).clearQueue();
	}

	//切换图片和缩略图

	function imgMove(){
		//判断是否是圆形循环，只支持渐变方式
		if(param.isCircular){
			if (index < imgNode.length - 1){
				classOper([imgNode,tabNode],defaultClass,true);
			}else{
				index = -1;
				classOper([imgNode,tabNode],defaultClass,true);

			}
		}else{
			if (direction == 1){
				if (index < imgNode.length - 1){
					classOper([imgNode,tabNode],defaultClass,true);
				}else{
					direction = 0;
					classOper([imgNode,tabNode],defaultClass,false);
				}
			}else{
				if (index > 0){
					classOper([imgNode,tabNode],defaultClass,false);
				}else{
					direction = 1;
					classOper([imgNode,tabNode],defaultClass,true);
				}
			}	
		}

		photoName.text(imgNode.eq(index).find("img").attr("title"));
		animate();
	}

	//鼠标移动显示左右移动箭头
	function triggerArrow(){
		var arrowLeft = $(_this).find(param.arrowLeft),arrowRight = $(_this).find(param.arrowRight);
		$(_this).bind({
			mouseover:function(){
				arrowLeft.show();
				arrowRight.show();
			},
			mouseout:function(){
				arrowLeft.hide();
				arrowRight.hide();
			}
		 });
	}

	//处理左右移动箭头
	function triggerDirection(){
		var arrowLeft = $(_this).find(param.arrowLeft),arrowRight = $(_this).find(param.arrowRight),
			arrowLeftOver = param.arrowLeftOver, arrowRightOver = param.arrowRightOver;

		arrowLeft.bind({
			click:function(){
				if(index != 0){// 判断当前是不是第一张
					classOper([imgNode,tabNode],defaultClass,false);
					animate();
				}else{
					//判断是否是圆形循环，只支持渐变方式
					if(param.isCircular){
						classOper([imgNode,tabNode],defaultClass,false);
						index = imgNode.length;
						classOper([imgNode,tabNode],defaultClass,false);
						animate();
					}
				}
				return false;
			},
			mouseover:function(){$(this).addClass(arrowLeftOver);},
			mouseout:function(){$(this).removeClass(arrowLeftOver);}
		});
		arrowRight.bind({
			click:function(){
				if(index < imgNode.length - 1){// 判断当前是不是最后一张
					classOper([imgNode,tabNode],defaultClass,true);
					animate();
				}else{
					//判断是否是圆形循环，只支持渐变方式
					if(param.isCircular){
						index = -1;
						classOper([imgNode,tabNode],defaultClass,true);
						animate();
					}
				}
				return false;
			},
			mouseover:function(){$(this).addClass(arrowRightOver);},
			mouseout:function(){$(this).removeClass(arrowRightOver);}
		});
	}

	//透明效果
	function transparent(){
		imgNode.animate({
			opacity: 0
		  }, 0, function() {
		  });
		$(_this).css({'background-color':imgNode.eq(index).attr('background')});
		imgNode.eq(index).animate({
			opacity: 1
		  }, 1000, function() {
			  _stop();
			  if(enterFlag) return;
			  time = setTimeout(imgMove, timer);
		  });
	}

	// 移动效果：每一张图片分10次移动
	function oneImgMove(){
		var nowMoveRange = (index * moveRange) - imgArea[0][scroll],
		partImgRange = nowMoveRange > 0 ? Math.ceil(nowMoveRange / 10) : Math.floor(nowMoveRange / 10);
		imgArea[0][scroll] += partImgRange;
		if (partImgRange == 0){
			imgNode.eq(index).addClass(defaultClass);
			tabNode.eq(index).addClass(defaultClass);
			photoName.text(imgNode.eq(index).find("img").attr("title"));
			partImgRange = null;
			_stop();
			if(!enterFlag)
				time = setTimeout(imgMove, timer);
		}
		else{
			partTime = setTimeout(oneImgMove,30);	
		}
		$(_this).css({'background-color':imgNode.eq(index).attr('background')});
	}

	//节点css类名操作 
	function classOper(arr,className,flag){
		arr.each(function(ind,n){
			n.eq(index).removeClass(className);
		});
		flag?(index++):(index--);
		arr.each(function(ind,n){
			n.eq(index).addClass(className);
		});
	}

}

base : (function(){
    var _timer = null,
                _sysTime = null,
                _countdownList = null;
            return function(arg){
                var _this = $(this),
                _args = jQuery.extend({
                    hasDay : true,
                    hasMinSec: false,
                    dayCnt : '',
                    hourCnt : '',
                    minuteCnt : '',
                    secondCnt : '',
                    minsecCnt : ''
                },arg || {}),
                _cut_time = null;

                function _init(){
                    if(!_args.countDownInfo) return;
                    _get_cut_time();
                    _this.data('cutTime',_cut_time).data('arg',_args).data('over',false);
                    _countdownList = $('[module-function="base"]').toArray();
                    _timer = null;
                    if(!_timer){
                        w.getServerTime(function(date){
                            _sysTime = date;
                             if(!_timer)
                                _count_down()
                        });
                    }
                }

                function _get_cut_time(){
                    var __cut_time_str = _args.countDownInfo,
                        __row_temp = __cut_time_str.split(' '),
                        __inplicit = __row_temp[0].split('-'),
                        __explicit = __row_temp[1].split(':');
                    _cut_time = new Date(Number(__inplicit[0]),(Number(__inplicit[1]) + 11)%12,Number(__inplicit[2]),Number(__explicit[0]),Number(__explicit[1]),Number(__explicit[2]));
                }

                function _get_count_down_item() {
                    var _result = [];
                    for (var i = 0, len = _countdownList.length ; i < len; i++) {
                        var __module = $(_countdownList[i]),
                            __cut_time = __module.data('cutTime'),
                            __arg = __module.data('arg'),
                            __left_time = parseInt((__cut_time - _sysTime)/1000);
                        if(__arg){
                            _result.push({
                                __module: __module,
                                __arg: __arg,
                                __left_time: __left_time
                            });
                        }
                    };
                    return _result;
                }

                function _count_down(){
                    var arr = _get_count_down_item();
                    for(var i = 0, len = arr.length; i < len; i++) {
                        (function(i){
                            var __module = arr[i].__module,
                            __arg = arr[i].__arg,
                            __left_time = arr[i].__left_time;
                            _timer = setInterval(function(){
                                if(__left_time < 0) {
                                    clearInterval(_timer);
                                    return;
                                }
                                var __day = Math.floor(__left_time/(24*3600)),
                                    __hour = Math.floor(__left_time/3600) - (__arg.hasDay?__day*24:0),
                                    __minute = Math.floor(__left_time%3600/60),
                                    __second = __left_time%60;
                                if(__arg.hasDay){
                                    __module.find(__arg.dayCnt).html(__day > 9?__day:'0' + __day);
                                }
                                __module.find(__arg.hourCnt).html(__hour > 9?__hour:'0' + __hour);
                                __module.find(__arg.minuteCnt).html(__minute > 9? __minute:'0' + __minute);
                                __module.find(__arg.secondCnt).html(__second > 9?__second:'0' + __second);
                                if(__arg.hasMinSec) {
                                    var j = 99;
                                    minTimer = setInterval(function(){
                                        if(__module.find(__arg.hourCnt).html() === '00' && __module.find(__arg.minuteCnt).html() === '00' && __module.find(__arg.secondCnt).html() === '00') {
                                            clearInterval(minTimer);
                                            __module.find(__arg.minsecCnt).html('00');
                                            return;
                                        }
                                        j = parseInt(j);
                                        if(j < 10) {
                                            j = '0' + j;
                                        }
                                        if (j == '00') {
                                            j = 99;
                                        }
                                        __module.find(__arg.minsecCnt).html(j);
                                        j--;
                                    }, 10);
                                }
                                __left_time--;
                            },1000);
                        })(i);
                    }
                }
                _init();

            };
})()

