base:(function(){
   			return function(arg){
   				var that = this,
   				 param = $.extend({
		 			hotnavs:"ul.hot-nav", //热门排行菜单
		 			hotnav:"li",          //热门排行菜单项
		 			hotnav_current:"current",  //当前选中菜单项的样式
		 			data_area:".hot-content",  //显示数据区域
		 			notice_area:".notice",     //显示更新时间区域
		 			wrap:".hot-wrap-module"
		 		},arg || {}),
                url= INTERFACE.rankData + '?jsonPara=';


              function _bandEvent(){
			 		//点击分类显示相应的内容
				 	$(that).find(param.hotnavs).delegate(param.hotnav,"click",function(){
				 		$(this).addClass(param.hotnav_current).siblings().removeClass(param.hotnav_current);
				 		//根据current显示ul
			    		var dataId = $(this).attr("data-id"),
			    		    _current_ul = $(that).find("ul.c"+dataId+"");
			    		_current_ul.fadeIn().siblings().hide();
			    		jshop.module.ridLazy(_current_ul);
				 	});

			 		var selectFirstCateId = $(that).find(".selectFirstCateId").val(),
			 		    type = $(that).find(".selectFirstCateId").attr("catetype");
			 		//根据分类判断请求:返回2级类目时，直接请求商品
			 		if(type == 2 && selectFirstCateId!="" && selectFirstCateId.split("-").length == 2){
			 			var cateIds = [];
			 			cateIds[selectFirstCateId.split["-"][1]] = $(that).find(".selectFirstCateId").attr("catename");
			 			var cateStr = "1-"+selectFirstCateId.split("-")[1];
			 			_ajaxGoods.call(that,cateIds,cateStr);
			 			return false;
			 		}
			 		//返回三级类目，不请求数据，隐藏该模块
			 		else if(type == 2 && selectFirstCateId!="" && selectFirstCateId.split("-").length == 3){
			 			$(that).hide();
			 			return false;
			 		}else if(type == 2 && selectFirstCateId=="1"){
						selectFirstCateId = "";
					}
			 		//其他情况，则是先请求二级类目，再根据类目请求商品数据
			 		_ajaxData.call(that,selectFirstCateId);
			 	}

			 	function _ajaxData(selectFirstCateId){
			 		var cateIds = [];//请求数据
			 			_url = url+'{"categoryIds":"'+selectFirstCateId+'"}&callback=?';

			 		//获取二级类目
			 		jQuery.ajax({
						  url: _url,
						  type:"get",
						  dataType:"jsonp",
						  success: function(data){
						  	if(data.code == "0"){
						  		var resLists = data.result.resList;
						  		var cateStr = "";//接口入参形式必须是一级分类-二级分类传递,实际上一级分类没任何实际用途，故此处随意取值1-key
						  		for(var c = 0; c < resLists.length; c++){
						  			categoryList = resLists[c].categoryList;
						  			for(var i = 0; i < categoryList.length; i++){						  			
						  				cateIds[categoryList[i].categoryId] = categoryList[i].categoryName;
						  				cateStr+="1-"+categoryList[i].categoryId+",";
						  			}
						  		}
						  		_ajaxGoods.call(that,cateIds,cateStr.slice(0,cateStr.length-1)); //请求二级类目下的商品
						  	}
						  }
					});
			 	}


			 	//获取二级类目下的商品
			 	function _ajaxGoods(cateIds,cateStr){
			 		var secondCates=[];
					if(cateStr == ""){
			 			$(that).hide();return;
			 		}
					jQuery.ajax({
						  url: url+'{"categoryIds":"'+cateStr+'"}&callback=?',
						  type:"get",
						  dataType:"jsonp",
						  success: function(data){
						    	if(data.code === "0"){
						    		var resLists = data.result.resList;
						    		//不存在商品，该模块不展示
						    		if(resLists.length == 0){
						    			$(that).hide();return;
						    		}
						    		var ul_str = [];
						    		for(var index = 0; index < resLists.length; index++){
						    			var resList = resLists[index],
						    			    categoryId = resList.categoryId,
						    			    _ul_str = '<ul class="item-list c'+categoryId+'">',
						    			    wares = resList.wares;
							    		if(wares.length > 0){
							    			var dataStr = [];

							    			for (var i = 0; i < wares.length; i++) {
												if(i >= 28){break;}
							    				var ware = wares[i],							    				   
							    				    _str = '<li data-sku="'+ware.sku+'"><div class="item-range r'+(i+1)+'">'+(+i+1)+'</div><a href=\'openapp.jdipad://virtual?params={"category":"jump","des":"productDetail","skuId":"'+ware.sku+'","sourceType":"yuni" ,"sourceValue":"yuni"}\'  target="_blank" class="item-pic"><img class="J_imgLazyload" src="//img14.360buyimg.com/cms/g10/M00/13/04/rBEQWFFj4PUIAAAAAAAESxyqJLUAADvdAIHC9oAAARj186.gif" original="'+ware.imgUrl+'"></a><a href=\'openapp.jdipad://virtual?params={"category":"jump","des":"productDetail","skuId":"'+ware.sku+'","sourceType":"yuni" ,"sourceValue":"yuni"}\' target="_blank" class="item-title" title="'+ware.title+'">'+ware.title+'</a><a href=\'openapp.jdipad://virtual?params={"category":"jump","des":"productDetail","skuId":"'+ware.sku+'","sourceType":"yuni" ,"sourceValue":"yuni"}\' target="_blank" class="item-price"><span> ¥ '+ware.price+'</span></a><a href=\'openapp.jdipad://virtual?params={"category":"jump","des":"cart","skuId":"'+ware.sku+'","skuNum":"1","sourceType":"yuni" ,"sourceValue":"yuni"}\' class="item-addcart" >加入购物车</a></li>';
							    				dataStr.push(_str);
							    			}
							    			_ul_str += dataStr.join("");

							    			//商品存在，显示二级类目
							    			if(secondCates.length == 0){
							    				secondCates.push('<li data-id="'+categoryId+'" cateId="'+categoryId+'" class="current"><a href="javascript:;">'+cateIds[categoryId]+'</a></li>');
							    				//更新时间,pc目前默认显示第一个分类的时间
									    		if(resList.updateTime){
									    			$(that).find(param.notice_area).find("span").text(resList.updateTime);	
									    			$(that).find(param.hotnavs).addClass("transalte68");				    			
									    			$(that).find(param.data_area).addClass("transalte68 item-list-mb");	
									    		}
							    			}else{
							    				secondCates.push('<li data-id="'+categoryId+'" cateId="'+categoryId+'"><a href="javascript:;">'+cateIds[categoryId]+'</a></li>');
							    			}
							    		}
							    		_ul_str += "</ul>";
							    		ul_str.push(_ul_str);

						    		}
						    		$(that).find(param.data_area).html(ul_str.join("")); //添加到item-list-wrap中
							    	$(that).find(param.hotnavs).html(secondCates.join("")); //添加菜单到nav中
						    		//根据current显示ul
						    		var dataId = $(that).find(param.hotnavs+" "+param.hotnav+".current").attr("data-id");
						    		var _current_ul = $(that).find("ul.c"+dataId+"");
						    		_current_ul.fadeIn();
						    		jshop.module.ridLazy(_current_ul);

						    	}else{
						    		$(that).find(param.notice_area).hide();
						    		$(that).find(param.data_area).hide();
						    	}
						  }
					});
				}

				_bandEvent.call(that);

   			};
   		})()

base : function(arg){
            var _this = $(this),
                _arg = $.extend({
                    serieId : '',
                    nameRecord : '.name',
                    timeRecord : '.time'
                },arg || {}),
                _data = null;
            function _init(){
                if(!_arg.serieId) return;
                _get_data();
                _get_img();
            }
            function _get_data(){
                var __url = INTERFACE.lottery.getWinnerList + '?';
                $.ajax({
                    url : __url,
                    type : 'GET',
                    cache : false,
                    data :{
                        lotteryCode : _arg.serieId,
                        count : 30
                    },
                    dataType : 'jsonp',
                    success : function(data){
                        if(data.responseCode == '0000'){
                            _data = data.data;
                            _render();
                        }
                        else{
                            alert(data.responseMessage);
                        }
                    }
                });
            }

            function _get_img(){
                if(navigator&&(navigator.userAgent.indexOf('iPad')>-1)){
                    var __url = INTERFACE.lottery.getLotteryInfo;
                    $.ajax({
                        url : __url,
                        data : {lotteryCode : _arg.serieId},
                        dataType : 'jsonp',
                        success : function(data){
                            if(data.responseCode == '0000'){
                                _this.find('.lucky-panel').prepend('<img style="height:100%;width:100%;"src="' + data.data.promptImg + '" />');
                                _this.find('embed').hide();
                            }
                        }
                    });
                }
            }

            function _render(){
            if(!_data) return;
                _data.each(function(index,n){
                    if(index == 0){
                        _this.find('.name').html(n.userPin);
                        _this.find('.time').html(n.prizeName);
                    }
                    else{
                        var __name_clone = _this.find('li:first').clone(true);
                        __name_clone.find('.name').html(n.userPin);
                        __name_clone.find('.time').html(n.prizeName);
                        _this.find('ul').append(__name_clone);
                    }
                });
                _this.find('.scroll-con').cntScroll();
            }   
            _init();
        }

