                var val = document.cookie.match(new RegExp("(^| )" + key + "=([^;]*)(;|$)"));
                return null != val ? unescape(val[2]) : null;
            }

            function doLottery(code, callback) {
                if (!code) {
                    callback && callback({
                        result: false,
                        msg: '缺少必要参数'
                    });
                }

                var lotApi = INTERFACE.lottery.lottery_start;

                jQuery.ajax({
                    url: lotApi,
                    type: 'post',
                    dataType: 'jsonp',
                    data: {
                        lotteryCode: code
                    },
                    success: function(data) {
                        if (data.responseCode === '0000') {
                            data.data.result = true;
                            // data.data.winner = true;
                            callback && callback(data.data);
                        } else {
                            callback && callback({
                                result: false,
                                msg: msgs[data.responseCode],
                                code: data.responseCode
                            });
                        }
                    }
                });
            }

