                            box: _this.find('.marquee'),
                            el: '.ui-marquee-main',
                            itemClass: '.ui-marquee-item',
                            interval: 80,
                            step: 2
                        });
                        scr.init();
                    } else {
                        throw(msgs[data.responseCode]);
                    }
                }
            });

            _this.find('.J_StartDraw').click(function(ev){
                ev.preventDefault();

                // if (!getCookie('pin')) {
                //     location.href = 'https://passport.jd.com/new/login.aspx?ReturnUrl=' + encodeURIComponent(location.href);
                //     return false;
                // }
                thick_login(function(){
                    var t = $(ev.currentTarget),
                        lotteryCode = encodeURIComponent(t.attr('data-code'));

                    if(_this.find('a.draw-btn').hasClass('slot-sign')){
                        return; 
                    }else{

                        jQuery.ajax({
                            url: INTERFACE.lottery.lottery_chance,
                            type: 'get',
                            dataType: 'jsonp',
                            data: {
                                lotteryCode: lotteryCode
                            },
                            success: function(data) {
                                if (!data.data || data.responseCode !== '0000') {
                                    tfl.find('p').html(data.data ? data.data.promptMsg : '');
                                    tfl.show();
                                    _this.find('a.draw-btn').removeClass('slot-sign');
                                    _this.find(".handle").removeClass('handled');
                                    return;
                                } else if (data.responseCode === '0000' && data.data.chances <= 0) {
                                    // tfn.show();
                                    tfl.find('p').html(data.data.promptMsg);
                                    tfl.show();
                                    _this.find('a.draw-btn').removeClass('slot-sign');
                                    _this.find(".handle").removeClass('handled');
                                    return;
                                }
                                doLottery(lotteryCode, function(json){
                                    var isWin = (json.result && json.winner),
                                        rs = [],
                                        str = '123',
                                        num = 2;
                                    _this.find('a.draw-btn').addClass('slot-sign');
                                    _this.find(".handle").addClass('handled');
                                    if (isWin) {
                                        num = +(str.split("")[Math.floor(Math.random() * 3)]);
                                        rs = [num, num, num];
                                    } else {
                                        rs[0] = +(str.split("")[Math.floor(Math.random() * 3)]);
                                        str = str.replace(rs[0] + '', "");
                                        rs[1] = +(str.split("")[Math.floor(Math.random() * 2)]);
                                        rs[2] = +(str.split("")[Math.floor(Math.random() * 2)]);
                                    }
                                    var soltItem0 = new Slot();
                                    soltItem0.el = _this.find(".solt-item-0");
                                    soltItem0.options.duration = 230;
                                    soltItem0.options.step = 2;
                                    soltItem0.options.result = rs[0];
                                    soltItem0.init();
                                    var soltItem1 = new Slot();
                                    soltItem1.el = _this.find(".solt-item-1");
                                    soltItem1.options.duration = 250;
                                    soltItem1.options.step = 2;
                                    soltItem1.options.result = rs[1];
                                    soltItem1.init();
                                    var soltItem2 = new Slot();
                                    soltItem2.el = _this.find(".solt-item-2");
                                    soltItem2.options.duration = 300;
                                    soltItem2.options.step = 2;
                                    soltItem2.options.result = rs[2];
                                    soltItem2.options.complete = function () {
                                        // $('.cover-succ').show();
                                        if (isWin) {
                                            tfg.show();
                                        } else {
                                            tfl.find('p').html(json.promptMsg);
                                            tfl.show();
                                        }
                                        _this.find('a.draw-btn').removeClass('slot-sign');
                                        _this.find(".handle").removeClass('handled');
                                    }
                                    soltItem2.init();
                                });
                            }
                        });
                    }
                });
            });

            tfg.click(function(){
                $(this).hide();
            });
            tfl.click(function(){
                $(this).hide();
            });
            tfn.click(function(){
                $(this).hide();
            });

            _this.find('a.draw-btn').mousedown(function () {
                if(_this.find('a.draw-btn').hasClass('slot-sign')){
                    return;
                }else{
                    $(this).addClass('drawed-btn');
                    _this.find(".handle").addClass('handled');

                }


