	//判断自定义编辑器里面的图片是否有链接  20150907 by cdwanchuan@jd.ccom
	if(isOwnLink && location.href.indexOf('visualEditing')!= -1){
		var img = node.find('img');
		img.each(function(i,e){
			if((jQuery(e).parent()[0].tagName!= 'A') && !jQuery(e).attr('usemap')){
				var relativePar = jQuery(jQuery(e)[0].offsetParent);
				if(relativePar.length && jQuery(e).length){
					var top = jQuery(e).offset().top - relativePar.offset().top + jQuery(e).height()/2 -11,
						left = jQuery(e).width()/2 - 31;
					node.append('<img src="//img11.360buyimg.com/cms/jfs/t2353/241/73326232/691/b8435b2b/55ed2da1N0a73abac.png" width="62" height="23" style="position:absolute; left:' + left +'px; top:' + top + 'px;" />');
				}
			}
		});
	}

sideSlip:function(args){

   	//此方法应用的图片不使用懒加载
	   var imgs = $(this).find('img');
	   imgs.each(function(index,n){
		  $(n).attr('src',$(n).attr('original'));
		  $(n).removeAttr('original');
		  $(n).removeClass('J_imgLazyload');
	   });

		if(args == undefined){
			if(validateData($(this).attr('module-param'))){
				var args = eval('('+$(this).attr('module-param')+')');
			}
		}

		var _this = this,
			param = $.extend({vertical:'top', verticalValue:170, horizontal:'left', horizontalValue:0, zindex:10},args),
			vertical = param.vertical,
			verticalValue = param.verticalValue,
			horizontal = param.horizontal,
			horizontalValue = param.horizontalValue,
			zindex = param.zindex;

		//判断参数个数
		var count = 0;
		for(var i in param){
			count++;
		}
		//如果垂直是top，水平是right
		if(count == 5 && vertical == 'top' && verticalValue >= 170 && horizontal == 'right'){
			setInterval(function(){
				verticalValue += (getTop() + param.verticalValue - verticalValue)/20;
				$(_this).css({'position':'absolute', 'top':verticalValue, 'right':horizontalValue, 'zIndex':zindex});
			},20);
		}
		//如果垂直是top，水平是left
		if(count == 5 && vertical == 'top' && verticalValue >= 170 && horizontal == 'left'){
			setInterval(function(){
				verticalValue += (getTop() + param.verticalValue - verticalValue)/20;
				$(_this).css({'position':'absolute', 'top':verticalValue, 'left':horizontalValue, 'zIndex':zindex});
			},20);
		}
		//如果垂直是bottom，水平是right
		if(count == 5 && vertical == 'bottom' && horizontal == 'right'){
			var a = $(window).height()-$(_this).height()-param.verticalValue,
				b = a;
			setInterval(function(){
				a += (getTop() + b - a)/20;
				$(_this).css({'position':'absolute', 'top':a, 'right':horizontalValue, 'zIndex':zindex});
			},20);
		}
		//如果垂直是bottom，水平是left
		if(count == 5 && vertical == 'bottom' && horizontal == 'left'){
			var a = $(window).height()-$(_this).height()-param.verticalValue,
				b = a;
			setInterval(function(){
				a += (getTop() + b - a)/20;
				$(_this).css({'position':'absolute', 'top':a, 'left':horizontalValue, 'zIndex':zindex});
			},20);
		}
		//如果垂直是bottom，水平是middle
		if(count == 5 && vertical == 'bottom' && horizontal == 'middle'){
			var a = $(window).height()-$(_this).height()-param.verticalValue,
				b = a;

			setInterval(function(){
				a += (getTop() + b - a)/20;
				$(_this).css({'position':'absolute','top':a, 'left':horizontalValue, 'margin-left':-($(_this).width()/2), 'zIndex':zindex});
			},20);
		}
		function getTop(){
			return (document.documentElement.scrollTop||document.body.scrollTop||0)-(document.documentElement.clientTop||document.body.clientTop||0);
		}
	}

base : (function(){
		var timer = null,
			auctionList = [],
			count = 0,
			tempList = [];
		return function(arg){
			var _this = this,
				_options = $.extend({
					status : ['wait','go','over'],
					item : 'li',
					timePanel : '.jNum',
					btnArea : '.jBtnArea a',
					priceNode : '.jdNum',
					process : '.jBgCurrent',
					text : '.jText',
					text1 : '.jText1',
					dataMsg : '.jDataMessage',
					day : '.days',
					hour : '.hours',
					minute : '.minutes',
					second : '.seconds',
					stock : '.stock',
					needDay : false
				},arg || {});

			function _unique(arr){
				var newArr = [],
					temp = {};

				for(var i = 0, len = arr.length; i < len;i++){
					if(!temp[arr[i]]){
						temp[arr[i]] = 1;
						newArr.push(arr[i]);
					}
				}
				return newArr;
			}

			function _getAuctionData(flag){
				var list = flag?tempList : auctionList,
					level = Math.ceil(list.length/10);
				for(var i = 0, len = level; i < len ; i++){
					var tempArr = [];
					for(var inner = i*10, innerLen = Math.min(list.length, (i+1)*10); inner < innerLen; inner ++){
						tempArr.push(list[inner]);
					}
					$.ajax({
						url : INTERFACE.paimai.currentList,
						type : 'POST',
						dataType : 'jsonp',
						data : {
							paimaiIds : tempArr.join('-')
						},
						success : function(data){
							_ajaxHandle(data);
						}
					});
				}
			}

			function _ajaxHandle(data){
				if(data){
					$.each(data,function(index,n){
						var __item = $('[paimaiId=' + n.paimaiId + ']');
						__item.closest(_options.item).attr('class',_options.status[n.auctionStatus]);
						__item.find(_options.priceNode).html(parseInt(n.currentPrice,10) <= 0 ? '暂无报价' : n.currentPrice);
						__item.find('.jRmb').html(n.currentPrice < 0 ? '' : '￥');
						__item.find('.startPrice').html(n.startPrice < 0 ? '暂无报价' : n.startPrice.toFixed(2));
						if(n.auctionType === 0){
                            __item.find('.minPrice').html(n.minPrice < 0 ? '暂无报价' : n.minPrice.toFixed(2));
                            if(n.auctionStatus == 2)
                                __item.find(_options.priceNode).html(n.minPrice < 0 ? '暂无报价' : n.minPrice.toFixed(2));
                        }
						if(n.auctionStatus == 0 || n.remainTime == - 1 || n.auctionStatus == 2){
							__item.find(_options.process).css('width','0%');
						}
						else{
							var __progress = (n.reduceTime*60*1000 - n.remainTime)/(n.reduceTime*60*1000);
							__item.find(_options.process).css('width',(__progress.toFixed(2)*100) + '%');
						}

						if(n.auctionStatus == 2){
							__item.find(_options.text).html('');
							__item.find(_options.text1).html();
							__item.find(_options.dataMsg).html(__item.find(_options.dataMsg).attr('endText'));
							__item.removeClass(_options.item.replace('.',''));
						}
						else{
							__item.find(_options.text).html(__item.find(_options.text).attr('defaultTxt'));
							__item.data('leftTime',parseInt(n.remainTime/1000));
							_showTime.call(__item);
							__item.find(_options.text1).html(n.auctionStatus == 0 || n.nextPrice == n.currentPrice || n.nextPrice < 0 ?'':__item.find(_options.text1).attr('defaultTxt'));
							__item.find(_options.dataMsg).html(n.auctionStatus == 0 ? __item.find(_options.dataMsg).attr('startTxt') : (n.nextPrice == n.currentPrice || n.nextPrice < 0?__item.find(_options.dataMsg).attr('endTxt'):'￥' + n.nextPrice.toFixed(2)));
							if(!!_options.stock)
								__item.find(_options.stock).html(n.currentNum);
						}
						var __btn = __item.find(_options.btnArea);
						if(n.auctionStatus == 1){
							__btn.attr('href',__btn.attr('detailUrl')).attr('target','_blank');
						}
						else{
							__btn.attr('href','#none').removeAttr('target');
						}
					});
				}		
			}

			function _showTime(){
				var __item = $(this),
					__left = __item.data('leftTime'),
					__second = __left%60,
					__minute = Math.floor(__left/60)%60,
					__hour = Math.floor(__left/3600)%24,
					__day = Math.floor(__left/60/60/24),
					__date = [];
				if(_options.needDay){
					__item.find(_options.day).html(__day>9?__day:'0'+__day)
				}
				else{
					__hour += __day*24;
				}
				__item.find(_options.hour).html(__hour>9?__hour:'0'+__hour);
				__item.find(_options.minute).html(__minute>9?__minute:'0'+__minute);
				__item.find(_options.second).html(__second>9?__second:'0'+__second);	
			}

			function _excute(){
				count = count%30;
				if(count == 0){
					_getAuctionData();
				}
				else{
					var items = $('[paimaiId]');
					items.each(function(index,n){
						var _remaindTime = $(n).data('leftTime');
						if(typeof _remaindTime !='undefined'){
							if(_remaindTime >0){
								_remaindTime --;
								$(n).data('leftTime',_remaindTime);
								_showTime.call($(n));
							}
							else{
								$(n).removeData('leftTime');
								tempList.push($(n).attr('paimaiId'));
							}
						}
					});
					if(tempList.length)
						_getAuctionData(true);
					tempList = [];
				}
				count ++;
			}

			function _init(){
				var __item =  $(_this).find(_options.item);
				if(__item.length === 0) return;

				if(timer){
					clearInterval(timer);
					timer = null;
					count = 0;
				}
				$(_this).find(_options.item).each(function(index,n){
					auctionList.push($(n).attr('paimaiId'));
				});
				auctionList = _unique(auctionList);
				timer = setInterval(function(){
					_excute();
				},1000);
			}
			_init();
		}
	})()

base: function(args) {
            var param = jQuery.extend({
                tabContainer: '.J_sort_tab',
                skuContainer: '.J_sort_content',
                tabNode: '.J_sort_tab .J_tab',  // tab节点（tab切换要用）
                tabContent: '.J_sort_content ul', // tab对应内容节点（tab切换要用）
                imgSize : 7,
                waitingCls: '.J_waiting',
                buynowCls: '.J_buy_now',
                overCls: '.J_over_time',
                defaultCls: 'J_default_item',
                diffCls: 'diff-item J_diff_item',
                modId: '',   // 模块实例id
                tagIndex: 0
            }, args),
                _this = jQuery(this),
                batch = {}; // 批次对象
            param.modId = _this.parent().parent().attr('instanceid');

            function formatDate(time) {
                var month = time.getMonth() + 1,
                    date = time.getDate(),
                    hours = time.getHours(),
                    minutes = time.getMinutes(),
                    result = '';
                month = month < 10 ? '0' + month : month;
                date = date < 10 ? '0' + date : date;
                hours = hours < 10 ? '0' + hours : hours;
                minutes = minutes < 10 ? '0' + minutes : minutes;
                result = month + '月' + date + '日&nbsp;' + hours + ':' + minutes;
                return result;
            }

            function  getImgServer(){
                var num = 10 + parseInt(Math.random()*4);
                return '//img' + num + '.360buyimg.com/n';
            }

            function getData() {
                jQuery.ajax({
                    url: 'http://act-jshop.jd.com/cfs.html',
                    data: {
                        modId: param.modId
                    },
                    dataType: 'jsonp',
                    success: function(data) {
                        if(data && data.result) {
                            batch = data.values;
                            if(typeof batch !== 'string' && batch != undefined) {
                                var tabContainer = _this.find(param.tabContainer),
                                    skuContainer = _this.find(param.skuContainer);
                                for (var i = 0; i < batch.length; i++) {
                                    (function(i){
                                        var beginTime = new Date(batch[i].beginTime.replace("-", "/").replace("-", "/")),
                                            endTime = new Date(batch[i].endTime.replace("-", "/").replace("-", "/")),
                                            nowTime = new Date(batch[i].nowTime.replace("-", "/").replace("-", "/")),
                                            batchTag;   // 标记批次状态，0表示正在进行，1表示未开始，2表示已结束
                                        if(
                                            nowTime.getTime() < endTime.getTime() &&
                                            nowTime.getTime() > beginTime.getTime()
                                        ) {
                                            // 表示该批次正在进行
                                            batchTag = 0;
                                            tabContainer.append(
                                                '<span class="J_tab now">' +
                                                    '<i></i>' +
                                                    '<span class="time">' + formatDate(beginTime) + '</span>' +
                                                    '<span class="word">正在秒杀</span>' +
                                                '</span>'
                                            );
                                            skuContainer.append('<ul batch-id="'+ batch[i].id +'"></ul>');
                                        } else if(
                                            nowTime.getTime() < endTime.getTime() &&
                                            nowTime.getTime() < beginTime.getTime()
                                        ) {
                                            // 表示该批次未开始
                                            batchTag = 1;
                                            tabContainer.append(
                                                '<span class="J_tab wait">' +
                                                    '<i></i>' +
                                                    '<span class="time">' + formatDate(beginTime) + '</span>' +
                                                    '<span class="word">未开始</span>' +
                                                '</span>'
                                            );
                                            skuContainer.append('<ul batch-id="'+ batch[i].id +'"></ul>');
                                        } else {
                                            // 批次已结束
                                            batchTag = 2;
                                            tabContainer.append(
                                                '<span class="J_tab now">' +
                                                    '<i></i>' +
                                                    '<span class="time">' + formatDate(beginTime) + '</span>' +
                                                    '<span class="word"></span>' +
                                                    '<span class="over">已结束</span>' +
                                                '</span>'
                                            );
                                            skuContainer.append('<ul batch-id="'+ batch[i].id +'"></ul>');
                                        }
                                        renderData(batchTag, batch[i]);
                                    })(i);
                                };
                                // 调用tab切换及去除图片懒加载模块方法
                                jshop.module.cateFlashSale.tab.call(_this[0], param);
                                jshop.module.cateFlashSale.ridLazy.call(_this[0], param.tabContent);
                            }
                        }
                    }
                });
            }

            function renderData(batchTag, batch) {
                // 渲染每个批次下商品信息
                for (var j = 0; j < batch.flashSalePromoList.length; j++) {
                    (function(j){
                        var skuItem = batch.flashSalePromoList[j],
                            beginTime = skuItem.timeBegin,
                            endTime = skuItem.timeEnd,
                            timer;
                        _this.find(param.skuContainer)
                        .find('[batch-id="' + batch.id + '"]')
                        .append(
                            '<li sku-id="' + skuItem.skuId + '" clstag="sale|keycount|' + param.modId + '|' + (param.tagIndex++) + '">' +
                                '<div class="goods-item">' +
                                    '<a class="goods-click" href="http://item.jd.com/' + skuItem.skuId + '.html" target="_blank"></a>' +
                                    '<div class="goods-num J_goods_num">限量<span>' + skuItem.numAvailable + '件</span></div>' +
                                    '<div class="goods-pic">' +
                                        '<img src="' + getImgServer() + param.imgSize + '/'+ skuItem.goodImg + '" alt="' + skuItem.goodName + '">' +
                                    '</div>' +
                                    '<div class="goods-info">' +
                                        '<div class="goods-name">' +
                                            '<span>' + skuItem.goodName + '</span>' +
                                        '</div>' +
                                        '<div class="goods-price">' +
                                            '<span class="price-tag">￥</span>' +
                                            '<span class="price-num">' + skuItem.promoPrice + '</span>' +
                                            '<a class="buy-now J_buy_now" href="http://item.jd.com/' + skuItem.skuId + '.html" target="_blank">' +
                                                '<span class="go"></span>' +
                                                '<span class="word">立即秒杀</span>' +
                                            '</a>' +
                                            '<a class="waiting J_waiting">未开始</a>' +
                                        '</div>' +
                                    '</div>' +
                                '</div>' +
                                '<div class="over J_over_time"></div>' +
                            '</li>'
                        );
                        var nowTime = new Date(batch.nowTime.replace("-", "/").replace("-", "/")),
                            currentItem = _this.find('[batch-id="' + batch.id + '"]').find('[sku-id="' + skuItem.skuId + '"]');
                        if(skuItem.numAvailable === 0) {
                            currentItem.find('.J_goods_num').html('不限量');
                        }
                        switch(batchTag) {
                            case 0:
                                // 批次正在进行，依次判断每个商品状态
                                // 判断商品秒杀时间,动态更改状态
                                if(nowTime.getTime() > endTime) {
                                    // 商品秒杀已结束
                                    currentItem.find(param.buynowCls).show();
                                    currentItem.find(param.overCls).show();
                                } else if(nowTime.getTime() < beginTime){
                                    // 商品秒杀未开始，首先显示未开始
                                    // 随后监听时间变化，到点变成立即秒杀按钮
                                    currentItem.find(param.waitingCls).show();
                                    var leftTime = beginTime - nowTime;
                                    timer = setInterval(function(){
                                        if(leftTime <= 0) {
                                            currentItem.find(param.waitingCls).hide();
                                            currentItem.find(param.buynowCls).show();
                                            clearInterval(timer);
                                        }
                                        leftTime--;
                                    }, 1000);
                                } else {
                                    // 商品正在秒杀，首先显示立即秒杀按钮
                                    // 随后监听时间变化，到点变成已结束
                                    currentItem.find(param.buynowCls).show();
                                    var leftTime = endTime - nowTime;
                                    timer = setInterval(function(){
                                        if(leftTime <= 0) {
                                            currentItem.find(param.overCls).show();
                                            clearInterval(timer);
                                        }
                                        leftTime--;
                                    }, 1000);
                                }
                                break;
                            case 1:
                                // 批次未开始，所有商品显示未开始状态
                                currentItem.find(param.waitingCls).show();
                                break;
                            case 2:
                                // 批次已结束，所有商品结束状态
                                currentItem.find(param.overCls).show();
                                break;
                            default:
                                return;
                                break;
                        }
                        // 根据mod 6结果添加样式（适应每行布局不一样模板）
                        if(j % 6 !== 0 && j % 6 !== 1) {
                            // 表示为4列一行的，添加样式
                            currentItem.addClass(param.diffCls);
                            // 调用自适应商品宽度模块方法
                            param.autoFillNode = '.J_diff_item';
                            param.minWidth = '240';
                            jshop.module.cateFlashSale.autoFill.call(
                                _this.find(param.skuContainer).find('[batch-id="' + batch.id + '"]')[0],
                                param
                            );
                        } else {
                            // 表示为2列一行的，添加样式
                            currentItem.addClass(param.defaultCls);
                            // 调用自适应商品宽度模块方法
                            param.autoFillNode = '.J_default_item';
                            param.minWidth = '490';
                            jshop.module.cateFlashSale.autoFill.call(
                                _this.find(param.skuContainer).find('[batch-id="' + batch.id + '"]')[0],
                                param
                            );
                        }
                    })(j);
                };
            }

            function bindRulesTrigger() {
                _this.find('.J_rules_trigger').bind('click', function(){
                    var rules = _this.find('.J_rules_content');
                    if(rules.hasClass('show')) {
                        rules.removeClass('show').slideUp();
                    } else {
                        rules.addClass('show').slideDown();
                    }
                });
                jQuery('body').bind('click', function(e){
                    var target = $(e.target);
                    if(!target.hasClass('J_rules_trigger')) {
                        var rules = _this.find('.J_rules_content');
                        if(rules.hasClass('show')) {
                            rules.removeClass('show').slideUp();
                        }
                    }
                });
            }

            function init() {
                getData();
                bindRulesTrigger();
            }

            init();
        }

