base : function(args){
            var _param=$.extend({node:'div'}, args || {}),
                _this = this,
                _prefixUrl = INTERFACE.soaYushou + '?sku=',
                _suffixUrl = '&callback=callBackService',
                _sku = _param.skuid,
                _remain_t= 0,
                _url = _prefixUrl + _param.skuid + _suffixUrl;

            w.callBackService = function(data){
                var __$this = $('[sku="' + data.sku + '"]'),
                    __arr = ['.presale','.appoint','.wait','.go','.over'];
                for(var i= 0;len=__arr.length,i<len;i++){
                    $(__$this).find(__arr[i]).addClass('hide');
                }
                data.state = data.state==0?5:data.state;
                $(__$this).find(__arr[data.state-1]).removeClass('hide');
                $('.has_appointed',__$this).addClass('hide');
                if(data.state==2){
                    if(data.flag==false){
                        $('.appoint').removeClass('wait2');
                        $(__arr[data.state-1]+">a",__$this).attr('href',data.url);
                        $(__arr[data.state-1]+">a",__$this).attr('target','_blank');
                    }else{
                        $(__arr[data.state-1]+">a",__$this).attr('href','#none');
                        $(__arr[data.state-1]+">a",__$this).attr('target','');
                        $('.appoint').addClass('wait2');
                        $('.appoint .button',__$this).unbind('click');
                    }
                    $('.has_appointed',__$this).removeClass('hide');
                    $('.has_appointed>span',__$this).html(data.num);
                }else if(data.state==4){
                    $(__arr[data.state-1]+">a",__$this).attr('href',data.url);
					$('.has_appointed>span',__$this).html(data.num);
                }
                if((data.state!=0) && (data.state!=5)){
                    _remain_t = data.d;
					if(_remain_t > 0)
						_start_timer(_remain_t,__$this);
                }

                $('.appoint .button',__$this).unbind('click');
                $('.appoint .button',__$this).click(function(){
                    if($(this).attr('href')=='#none' || !$(this).attr('href')){
                        return false;
                    }
                    var r = window.open($(this).attr('href'));
                    if(r){
                        $('.appoint',__$this).addClass('wait2');
                        $('.appoint>a',__$this).attr('target','');
                        $('.appoint>a',__$this).attr('href','#none');
                        $('.appoint .button',__$this).unbind('click');
                    }
                    return false;
                });
            }

            function _start_timer(t,arg){
                var __mode =  arg,
                    _remain_t = t,
                    __instanceid = __mode.parents('[instanceid]').attr('instanceid');
                if(w.jshop.module.forwardSale.modeTimer[__instanceid])
                    clearInterval(w.jshop.module.forwardSale.modeTimer[__instanceid]);
                w.jshop.module.forwardSale.modeTimer[__instanceid] = setInterval(function(){
                    if(_remain_t <= 0)
                        clearInterval(w.jshop.module.forwardSale.modeTimer[__instanceid]);
                    var __tt = _remain_t,
                        __seconds = parseInt(__tt)%60,
                        __minutes = parseInt((__tt) / 60) % 60,
                        __hours = parseInt((__tt / 60 / 60) % 24),
                        __days = parseInt(__tt / 60 / 60 / 24);

                    __days < 10 ? __days = "0" + __days : __days = __days;
                    __hours < 10 ? __hours = "0" + __hours : __hours = __hours;
                    __seconds < 10 ? __seconds = "0" + __seconds : __seconds = __seconds;
                    __minutes < 10 ? __minutes = "0" + __minutes : __minutes = __minutes;
                    if((__days==0 || __days=='00') && (__hours==0 || __hours=='00')  && (__minutes==0 || __minutes=='00') && (__seconds==0 || __seconds=='00')){
                        var __arg = eval('(' + __mode.attr('module-param') + ')');
                        w.jshop.module.forwardSale.base.call(__mode,__arg);
                    }
                    $('.days', __mode).html(__days);
                    $('.hours',__mode).html(__hours);
                    $('.minutes',__mode).html(__minutes);
                    $('.seconds',__mode).html(__seconds);
                    _remain_t -= 1;
                },1000);
            }

            function _init(){
                if(_sku){
                    $.getScript(_url);
                }
            }

            _init();
        }

batchPhase1 : function(arg){
			var that = this,
				options = $.extend({
					isFillZero : true,
					item:'.item',
					defaultCls : 'd-current',
					isNeedSecond : false,
					day : '.days',
					hour : '.hours',
					minute : '.minutes',
					second : '.seconds',
					hasAppoint : '.has_appointed>span',
					button : '.button'
				},arg),
				instanceid = $(that).parents('[instanceid]').attr('instanceid'),
				duration = options.isNeedSecond ? 1000 : (60*1000),
				countdownItem = [];


			var item = $(that).find(options.item),
				len = item.length,
				count = 0;
			if(!len) return

			function getData(){
				item.each(function(index,n){
					var node = $(n);
					$.ajax({
						url : INTERFACE.soaYushou + '?sku=' + node.attr('saleid'),
						dataType : 'jsonp',
						success : function(data){
							var __arr = ['presale','appoint','wait','go','over'];
							data.state = data.state==0?5:data.state;
							node.addClass(__arr[data.state - 1]).addClass(options.defaultCls);
							if(data.state === 2){
								if(data.flag){
									node.find(options.button).attr('href','#none').attr('target','');
									node.addClass('wait2');
								}else{
									node.removeClass('wait2');
									node.find(options.button).attr('href',data.url).attr('target','_blank');
								}
								node.find(options.hasAppoint).html(data.num);
							}

							if(data.state == 4)
								node.find(options.button).attr('href',data.url);
								node.find(options.hasAppoint).html(data.num);
								node.find(options.button).unbind('click').click(function(){
								 if($(this).attr('href')=='#none' || !$(this).attr('href')){
									return false;
								}
								var __r = window.open($(this).attr('href'));
								if(__r){
									node.addClass('wait2');
									node.children('a').attr('target','').attr('href','#none');
									$(this).unbind('click');
								}
								return false;
							});
							count ++;
							if(data.state != 5){
								node.data('left',data.d);
								if(data.d > 0)
									countdownItem.push(node);
								if(count == len)
									countdown();
							}
						}
					});
				});
			}

			function countdown(){
				handle();
				jshop.module.forwardSale.modeTimer[instanceid] = setInterval(handle,duration);

			}

			function init(){
				if(jshop.module.forwardSale.modeTimer[instanceid])
					clearInterval(jshop.module.forwardSale.modeTimer[instanceid]);
				count = 0;
				countdownItem = [];
				getData();	
			}

			function handle(){
				var hasStep = false;
				for(var i= 0 , len = countdownItem.length; i < len; i++){
					var node = countdownItem[i],
						left = node.data('left'),
						days = Math.floor(left/(60*24*60)),
						hours = Math.floor(left/3600)%24,
						minutes = Math.floor(left/60)%60,
						seconds = left%60;

					if(options.isFillZero){
						days = days > 9 ? days : '0' + days;
						hours = hours > 9 ? hours : '0' + hours;
						minutes = minutes > 9 ? minutes : '0' + minutes; 
						seconds = seconds > 9 ?seconds : '0' + seconds;
					} 
					node.find(options.day).html(days);
					node.find(options.hour).html(hours);
					node.find(options.minute).html(minutes);

					if(options.isNeedSecond){
						node.find(options.second).html(seconds);
					}
					if(options.isNeedSecond){
						left --;
					}
					else{
						left -= 60;
					}
					node.data('left',left);
					if(left <=0){
						hasStep = true;
					}
				}

				if(hasStep)
					init();
			}

			init();

		}

