        }

showImg: function(args) {
            var silde = true;
			// 定义传入的CSS调用变量
			var param=$.extend({timer:0,node:'li', nodeChild:'.jItem', imgNodeArea:'.jPic', imgNode:'.jPic a', defaultClass:'current', slideDirection:'left', subFunction:'moveEffect'}, args),
				_this=this,
				imgArea = $(_this).find(param.node),
				nodeChild = $(_this).find(param.nodeChild),
				defaultClass = param.defaultClass,
				currentNode = imgArea.eq(0).find(param.imgNode),
				_duration = parseInt(parseFloat(param.timer || 0)*1000);

			if(!imgArea.length) return;

			//全局变量
			var index = 0,moveRange = 0,partTime = null,animate = null;
			var isTop = (param.slideDirection == 'top')?true:false;
            var enterFlag =[],count = 0;

			//轮播图所有效果
			var banner = {
				transparentEffect : function(){
					// 调用函数
					init();
					animate = transparent;
					_event();
				},
				moveEffect : function(){
					//初始化
					imgArea.each(function(i,n){
						var imgNodeArea = $(n).find(param.imgNodeArea);
						if(isTop){
							imgNodeArea.css({height:100000});
							imgNodeArea.children().css({width:imgNodeArea.width(),height:"auto","float":"none",display:"block"});
						}else{
							imgNodeArea.css({width:100000});
							imgNodeArea.children().css({width:imgNodeArea.find('img').width(),height:"100%","float":"left",display:"block"});//将这个宽度写在css里，在ie6下面，获取到的父级宽度是被这个元素撑开的宽度
						};
					});

					// 调用函数
					init();
					animate = oneImgMove;
					_event();
				}
			};

			//根据传入的子方法名执行对应的子方法

			 jshop.module.ridLazy(_this);
			if(banner[param.subFunction])
				banner[param.subFunction].call(_this);

			//轮播图初始化
			function init(){
				imgArea.css({width:currentNode.find('img').width(),height:currentNode.find('img').height()});
				nodeChild.css({width:currentNode.find('img').width(),height:currentNode.find('img').height()});
			}

			 function _event(){
                imgArea.each(function(index,n){
                	var _ele = $(n);
                    _ele.data('index',0);
                	_ele.data('count',0);
                	_ele.data('animating',false);
					_ele.data('direction',1);

                	_ele.bind({
                        mouseenter:function(){
							var _this = this,_area = $(this),_count = _area.data('count') + 1, _node = _area.find(param.imgNode);
							_area.data('count',_count),currentNode = _area.find(param.imgNode);
							if(!_area.data('animating')){
								_area.data('direction',1);
								_area.data('index',1);
								currentNode.removeClass(defaultClass).eq(1).addClass(defaultClass);	
								animate.call(_this);
							}			
						},
                        mouseleave:function(){
							var _this = this,_area = $(this),_count = _area.data('count') + 1, _node = _area.find(param.imgNode);
							_area.data('count',_count),currentNode = _area.find(param.imgNode);
							if(!_area.data('animating')){
								_area.data('direction',2);
								_area.data('index',0);
								currentNode.removeClass(defaultClass).eq(0).addClass(defaultClass);	
								animate.call(_this);
							}	   
						}
                    });
                });
			 }

			//透明效果
			function transparent(){

				var _this = this, _area = $(_this),_index = _area.data('index');
					_currentnode = _area.find(param.imgNode); 

				_area.data('animating',true);
				_currentnode.animate({
					opacity: 0
				  }, 0, function() {
				  });
				_currentnode.eq(_index).animate({
					opacity: 1
				  }, _duration, function() {
						_area.data('animating',false);
						var _count = _area.data('count'), _direction = _area.data('direction');
						if(_direction == 1){
							if(_count%2 == 0){
								_area.data('direction',2);
								_area.data('index',0);
								_area.find(param.imgNode).removeClass(defaultClass).eq(0).addClass(defaultClass);	
								animate.call(_this);
							}
						}
						else{
							if(_count%2 == 1){
								_area.data('direction',1);
								_area.data('index',1);
								_area.find(param.imgNode).removeClass(defaultClass).eq(1).addClass(defaultClass);	
								animate.call(_this);		
							}
						}
				  });
			}

			//移动效果：每一张图片分10次移动

			function oneImgMove(){
				var _this = this,_area = $(_this),_css = {},
					_index = _area.data('index');
				_area.data('animating',true);

				if(isTop){
					_css.marginTop = -_index*_area.find(param.imgNode).height() + 'px';
				}
				else{
					_css.marginLeft = -_index*_area.find(param.imgNode).width() + 'px';
				}

				_area.find(param.imgNodeArea).animate(_css,_duration,function(){
					_area.data('animating',false);
					var _count = _area.data('count'), _direction = _area.data('direction');
					if(_direction == 1){
						if(_count%2 == 0){
							_area.data('direction',2);
							_area.data('index',0);
							_area.find(param.imgNode).removeClass(defaultClass).eq(0).addClass(defaultClass);	
							animate.call(_this);
						}
					}
					else{
						if(_count%2 == 1){
							_area.data('direction',1);
							_area.data('index',1);
							_area.find(param.imgNode).removeClass(defaultClass).eq(1).addClass(defaultClass);	
							animate.call(_this);		
						}
					}
				});
			}
        }

base : (function(){
			timer = {};
			return function(arg){
				var that = this,
					root = $(that),
					options = $.extend({
						node : 'li',
						waitCls : 'jWait',
						underwayCls : 'jGo',
						overCls : 'jOver',
						hour : '.jHours',
						minute : '.jMinutes',
						second : '.jSeconds',
						isWaitCount : false,
						waitHour : '.jHours',
						waitMinute : '.jMinutes',
						waitSecond : '.jSeconds',
						goodsImg : '.jPic img',
						goodsName : '.jDesc a',
						price : '.jdPrice .jNum',
						numAvailable : '.jNumAvailable em',
						availableNode : '.jNumAvailable',
						levelPrifix : 'jLevel',
						level : '.jLevelMember',
						levelName : '.jLevelMember .jText',
						priceAttr : 'price',
						link : '.jPic a',
						imgSize : 7,
						levelDesSuf : '及以上专享',
						noPrice : '暂无报价',
						btn : '.jCart'
					},arg),
					node = root.find(options.node),
					instanceid = root.closest('[instanceid]').attr('instanceid'),
					waited = !1,phaseTwoLeft = 0,
				level = [{
					name : '注册',
					level : 50
				},{
					name : '铜牌',
					level : 56
				},{
					name : '银牌',
					level : 61
				},{
					name : '金牌',
					level : 62
				},{
					name : '钻石',
					level : 105
				},{
					name : 'VIP',
					level : 110
				},{
					name : '企业',
					level : 90
				}],timeLeft = 0;

				function getInfo(){
					$.ajax({
						url : INTERFACE.actJshop.fs,
						data : {
							modId : instanceid
						},
						dataType : 'jsonp',
						success : function(data){
							if(data&&data.result){
								data = data.values;
								if(!!data.skuId){
									var sku = data.skuId;
									fill(options.price,'html',data.promoPrice < 0 ? options.noPrice : data.promoPrice);
									fill(options.goodsImg,'src',getImgServer() + options.imgSize + '/'+ data.goodImg);
									fill(options.goodsName,'html',data.goodName);
									if(!!data.numAvailable)
										fill(options.numAvailable,'html',data.numAvailable);
									else root.find(options.availableNode).hide();
									fill(options.link,'href', INTERFACE.linkGoods + '/' + sku + '.html');
									var levelDes = getLevel(data.levelMember);
									fill(options.levelName,'html',levelDes.name + options.levelDesSuf);
									root.find(options.level).addClass(options.levelPrifix + levelDes.level);
									if(data.status == '1'){
										node.attr('class',options.underwayCls);
										var left =  getNowTime(data.endTime) - getNowTime(data.nowTime);
										timeLeft = parseInt(left/1000);
										count();
									}
									else if(data.status == '0'){
										node.attr('class',options.overCls);
									}
									else{
										node.attr('class',options.waitCls);
										if(options.isWaitCount){
											waited = true;
											var left = getNowTime(data.startTime) - getNowTime(data.nowTime);
											timeLeft = parseInt(left/1000);

											var left2 = getNowTime(data.endTime) - getNowTime(data.startTime);
											phaseTwoLeft = parseInt(left2/1000);
											count();
										}
									}
									node.find('[' + options.priceAttr + ']').each(function(ind,q){
										$(q).attr($(q).attr(options.priceAttr),data.skuId);
										$(q).attr('jshop','price');
									});
									jshop.module.ridLazy(node);
									node.find(options.btn).attr('href', INTERFACE.linkCart + '?pid=' + data.skuId + '&pcount=1&ptype=1');
								}
								else{
									node.attr('class',options.overCls);
								}
							}
						}
					});
				}

				function  getImgServer(){
					var num = 10 + parseInt(Math.random()*4);
					return '//img' + num + '.360buyimg.com/n';
				}

				function getNowTime(str){
					var a = str.split(' '),
						a1 = a[0].split('-'),
						a2 = a[1].split(':'),
						date = new Date(a1[0],parseInt(a1[1]) - 1,a1[2],a2[0],a2[1],a2[2]);
					return date.getTime();
				}

				function getLevel(le){
					var result = null;
					for(var i = 0, len = level.length; i < len; i++){
						if(level[i].level == le){
							result = level[i];
							break;
						}
					}
					return result;
				}

				function fill(search,attr,value){
					if(node.find(search).length){
						if(attr == 'html'){
							node.find(search).html(value);
						}
						else{
							node.find(search).attr(attr,value);
						}
					}
				}

				function count(){
					handle();
					if(timer[instanceid]){
						clearInterval(timer[instanceid]);
						timer[instanceid] = null;
					}
					timer[instanceid] = setInterval(function(){
						handle();
					},1000);
				}

				function handle(){
					if(timeLeft <= 0){
						if(waited){
							node.attr('class',options.underwayCls);
							timeLeft = phaseTwoLeft;
							waited = false;
						}
						else{
							node.attr('class',options.overCls);
							clearInterval(timer[instanceid]);
							timer[instanceid]  = null;
						}

					}
					else{
						var hour = parseInt(timeLeft/3600),
							minute = parseInt(timeLeft/60)%60,
							second = timeLeft%60;
						hour = hour < 10 ? '0' + hour : hour;
						minute = minute < 10 ? '0' + minute : minute;
						second = second < 10 ? '0' + second : second;
						root.find(waited?options.waitHour:options.hour).html(hour);
						root.find(waited?options.waitMinute:options.minute).html(minute);
						root.find(waited?options.waitSecond:options.second).html(second);
					}
					timeLeft -- ;
				}

				function init(){
					jshop.module.ridLazy(node);
					getInfo();
				}

				init();
			};
		})()

addEllipsis:function(args){

       if(!args)return;
       var _this = this,
               param = jQuery.extend({title:'li', count:20, text:'...'}, args),
               elem = jQuery(_this).find(param.title),
               reg = /\s|\,|\.|\!|\'|\"|\;|\:|\t|\n|\r/g;
//               reg = new RegExp("\\s|\\,|\\.|\\!|\\'|\\\"|\\;|\\:|\\t|\\n|\\r", "g");
       elem.each(function(index, ele){
           var _textNode = ele.firstChild,
                   _textValue = _textNode.nodeValue;
           if(_textNode && _textNode.nodeType == 3 && _textNode.length >= param.count){
               var _tempValue = _textValue.substring(0, param.count - param.text.length);
               var _char = _textValue.charAt(param.count - param.text.length);
               if(reg.test(_char)){
                   _textNode.nodeValue = _tempValue + param.text;
               }else{
                   var _arr = _tempValue.match(reg);
                   _tempValue = _tempValue.substring(0, _tempValue.lastIndexOf(_arr[_arr.length - 1]) + 1) + param.text;
                   _textNode.nodeValue = _tempValue;
               }
           }
       });

   }

