                                successHandle(data);
                            }else{
                                //如果第一次拉取数据失败，会尝试再进行一次拉取。选品接口目前还不够稳定
                                if(!jTarget.data('send')){
                                    jTarget.data('send', true);
                                    tool.getChoiceLot();
                                }else{
                                    console.error('获取选品接口响应异常：' + data);
                                }
                            }
                        }
                    });
                },

                //发送埋点请求
                //@param type   埋点类型['show','detail','cart']。分别代表列表展示、打开商品详情页、加入购物车
                //@param data   接口响应的数据
                //@param skus   sku值，多个以英文逗号隔开
                sendLog: function(type, data, skus){

                    var paramVal = '',
                        url = data.mercuryUrl,
                        os = (function(){
                            var val = 'other';
                            if((navigator.platform == "Win32") || (navigator.platform == "Windows")){
                                val = 'win';
                            }else if((navigator.platform == "Mac68K") || (navigator.platform == "MacPPC") || (navigator.platform == "Macintosh")){
                                val = 'mac';
                            }
                            return val;
                        })();

                    //展示列表的埋点请求
                    if(type === 'show'){
                        paramVal = 'action=0$';
                    }else if(type  === 'detail'){
                        //跳转商品详情页
                        paramVal = 'action=1$';
                    }else if(type === 'cart'){
                        //点击加入购物车
                        paramVal = 'action=4$';
                    }

                    //补全埋点需要的各种参数值
                    url = url.replace(/mercury\.jd\.local/g, 'mercury.jd.com');
                    url = url.replace(/action=\d?\$/, paramVal);
                    url = url.replace(/uip=/, 'uip=' + data.clientIp);
                    url = url.replace(/csku=[\d,]*\$/, 'csku=' + skus + '$');
                    url = url.replace(/channel=/, 'channel=' + 1);
                    url = url.replace(/\$os=/, '$os=' + os);
                    url = url.replace(/cul=/, 'cul=' + encodeURIComponent(location.href));
                    url = url.replace(/ref=/, 'ref=' + document.referrer);
                    url = url.replace(/&rm=/, '&rm=' + new Date().getTime());
                    //ware_type是1（人工选品）2（大脑选品）。目前只有大脑需要发送埋点请求
                    url = url.replace(/&cul/, '$ware_type=2&cul');

                    var img = new Image();
                    img.src = url;
                }
            }

            //成功回调函数
            function successHandle(data){
                var html = '',
                    jTpl = jTarget.find(param.itemTempSelector),
                    tpl = jTpl.html(),
                    arrData = data.data,
                    arrSku = [];
                jTpl.remove();
                for(var i = 0; i < arrData.length; i++){
                    arrData[i].clstag = 'clstag="sale|keycount|' + instanceid + '|' + (i + 1) + '"';
                    html += tool.replace(tpl, arrData[i]);
                    var sku = parseFloat(arrData[i].goodsId);
                    if(!isNaN(sku) && sku > 0){
                        arrSku.push(sku);
                    }
                }
                jTarget.find(param.containerSelector).html(html);

                param.log && logHandle(arrSku, data);

                //释放图片
                jshop.module.ridLazy(jTarget);

                //触发价格懒加载
                jshop.component.getPrice(jTarget);

                //如果模板里声明了autoFill，则触发autoFill的调用
                jTarget.attr('module-function').match(/autoFill/) && jshop.module.autoFill.call(jTarget,  param);
            }

            //处理埋点相关
            function logHandle(arrSku, data){
                //必须要有mercuryUrl才认为是大脑数据，才进行埋点
                if(arrSku.length && data.mercuryUrl){

                    //绑定点击sku的相关埋点
                    var jWrap = jTarget.find(param.containerSelector),
                        skuMap = (function(){
                                var obj = {};
                                for(var i = 0; i < data.length; i++){
                                    obj[data[i].goodsId] = data[i].groupId;
                                }
                                return obj;
                            })();

                    tool.sendLog('show', data, arrSku.join(','));

                    //点击跳转商品详情页
                    jWrap.find(param.logDetailSelector).bind('click', function(){
                        var thisSku = $(this).attr('href').match(/item\.jd\.com\/(\d+)\./);
                        if(thisSku){
                            thisSku = thisSku[1];
                            tool.sendLog('detail', data, thisSku);
                        }
                    });

                    //加入购物车
                    jWrap.find(param.logCartSelector).bind('click', function(){
                        var thisSku = $(this).attr('href').match(/pid=(\d+)&/)[1];
                        if(thisSku){
                            thisSku = thisSku[1];
                            tool.sendLog('cart', data, thisSku);
                        }
                    });
                }

            }

            //数据初始化
            function dataInit(){
                tool.getChoiceLot();
            }

            //初始化入口
            !function init(){
                if(param.rid && !isNaN(parseFloat(param.rid))){
                    dataInit();
                }
            }();
        }

