                    classOper([imgNode,tabNode],defaultClass,true);
                }else{
                    index = -1;
                    classOper([imgNode,tabNode],defaultClass,true);

                }
            }else{
                if (direction == 1){
                    if (index < imgNode.length - 1){
                        classOper([imgNode,tabNode],defaultClass,true);
                    }else{
                        direction = 0;
                        classOper([imgNode,tabNode],defaultClass,false);
                    }
                }else{
                    if (index > 0){
                        classOper([imgNode,tabNode],defaultClass,false);
                    }else{
                        direction = 1;
                        classOper([imgNode,tabNode],defaultClass,true);
                    }
                }
            }
            animate();
        }

        /**
        鼠标移动显示左右移动箭头

        function triggerArrow(){
            var arrowLeft = $(_this).find(param.arrowLeft),arrowRight = $(_this).find(param.arrowRight);
            $(_this).bind({
                mouseover:function(){
                    arrowLeft.show();
                    arrowRight.show();
                },
                mouseout:function(){
                    arrowLeft.hide();
                    arrowRight.hide();
                }
             });
        }

        /**
        处理左右移动箭头

        function triggerDirection(){
            var arrowLeft = $(_this).find(param.arrowLeft),arrowRight = $(_this).find(param.arrowRight),
                arrowLeftOver = param.arrowLeftOver, arrowRightOver = param.arrowRightOver;

            arrowLeft.bind({
                click:function(){
                    if(index != 0){// 判断当前是不是第一张
                        classOper([imgNode,tabNode],defaultClass,false);
                        animate();
                    }else{
                        //判断是否是圆形循环，只支持渐变方式
                        if(param.isCircular){
                            classOper([imgNode,tabNode],defaultClass,false);
                            index = imgNode.length;
                            classOper([imgNode,tabNode],defaultClass,false);
                            animate();
                        }
                    }
                   return false;
                },
                mouseover:function(){$(this).addClass(arrowLeftOver);},
                mouseout:function(){$(this).removeClass(arrowLeftOver);}
            });
            arrowRight.bind({
                click:function(){
                    if(index < imgNode.length - 1){// 判断当前是不是最后一张
                        classOper([imgNode,tabNode],defaultClass,true);
                        animate();
                    }else{
                        //判断是否是圆形循环，只支持渐变方式
                        if(param.isCircular){
                            index = -1;
                            classOper([imgNode,tabNode],defaultClass,true);
                            animate();
                        }
                    }
                   return false;
                },
                mouseover:function(){$(this).addClass(arrowRightOver);},
                mouseout:function(){$(this).removeClass(arrowRightOver);}
            });
        }

        /**
        透明效果

        function transparent(){
            imgNode.animate({
                opacity: 0
              }, 0, function() {
              });
            $(_this).css({'background-color':imgNode.eq(index).attr('background')});
            imgNode.eq(index).animate({
                opacity: 1
              }, 1000, function() {
                  _stop();
                  if(enterFlag)return;
                  time = setTimeout(imgMove, timer);
              });

        }

        /**
        移动效果：每一张图片分10次移动

        function oneImgMove(){
            var nowMoveRange = (indexmoveRange) - imgArea[0][scroll],
            partImgRange = nowMoveRange > 0 ? Math.ceil(nowMoveRange / 10) : Math.floor(nowMoveRange / 10);
            imgArea[0][scroll] += partImgRange;
            if (partImgRange == 0){
                imgNode.eq(index).addClass(defaultClass);
                tabNode.eq(index).addClass(defaultClass);
                partImgRange = null;
                _stop();
                if(!enterFlag)
                    time = setTimeout(imgMove, timer);
            }
            else{
                partTime = setTimeout(oneImgMove,30);
            }
            $(_this).css({'background-color':imgNode.eq(index).attr('background')});
        }

        /**
        节点css类名操作

        function classOper(arr,className,flag){
            arr.each(function(ind,n){
                n.eq(index).removeClass(className);
            });
            flag?(index++):(index--);
            arr.each(function(ind,n){
                n.eq(index).addClass(className);
            });
        }
    }

specialAuction: function(param){
            var jTarget = $(this),
                param = $.extend({
                    idNode:'.item',
                    bidNode:'.j-bid-count',
                    accessNode:'.j-access-count',
                    startNode:'j-start-time',
                    endNode:'j-end-time',
                    waitClass:'wait',
                    goClass:'go',
                    overClass:'over'
                },param || {}),
                baseurl = INTERFACE.paimai.queryCurAlbumInfo;

            function getInfo(ids){
                $.ajax({
                    url : baseurl,
                    data: {albumIdStr: ids},
                    dataType : 'jsonp',
                    success : function(data){
                        //  [{"bidCount":0,"access":1},{"bidCount":0,"access":1},{"bidCount":0,"access":3}]
                        if(data && data.length){
                            getServerTime(function(nowDate){
                                var nowDate = new Date(nowDate),
                                    nowTime = nowDate.getFullYear() + "-" + (nowDate.getMonth() + 1) + "-" + nowDate.getDate() + " " + nowDate.getHours() + ":" + nowDate.getMinutes() + ":" + nowDate.getSeconds(),
                                    jItems = jTarget.find(param.idNode);

                                for(var i = 0; i < data.length; i++){
                                    var jItem = jItems.eq(i),
                                        startTime = jItem.find(param.startNode).val(),
                                        endTime = jItem.find(param.endNode).val();
                                    jItem.find(param.bidNode).text(data[i].bidCount);
                                    jItem.find(param.accessNode).text(data[i].access);
                                    if(startTime && endTime){
                                        var startDate = new Date(startTime.replace(/-/g, "/").replace(/\.\d/, "")),
                                            endDate = new Date(endTime.replace(/-/g, "/").replace(/\.\d/, ""));
                                        if(startDate - nowDate > 100){
                                            new Countdown(jItem, param, [{"countDownTime": startTime, "class": param.waitClass}, {"countDownTime": endTime, "class": param.goClass}, {"class": param.overClass}], i).init();
                                        }else if(endDate - nowDate > 100){
                                            new Countdown(jItem, param, [{"countDownTime": endTime, "class": param.goClass}, {"class": param.overClass}], i).init();
                                        }else{
                                            jItem.addClass(param.overClass);
                                        }
                                    }
                                }
                            });
                        }
                    }
                });
            }

            //倒计时对象
            //@private jContainer          容器div
            //@private param               各种倒计时相关参数
            //@private duration            一个数组，里面包含了倒计时的各个区间段，可以实现到点自动切换。格式：[{"countDownTime": "", "class": param.waitClass}*N]
            //@private timerIndex          倒计时在模块中的序号，用于唯一标示倒计时的timer
            function Countdown(jContainer, param, duration, timerIndex) {
                var interval = 60000,
                    setting = {
                        hasDay: true,
                        dayCnt: '.days',
                        timerContainer: '.jTime',
                        hourCnt: '.hours',
                        minuteCnt: '.minutes',
                        secondCnt: '.seconds'
                    },
                    nowTime = 0,
                    beginTime = 0,
                    endTime = 0,
                    jDay,
                    jHour,
                    jMinu,
                    jSeco,
                    jTime,
                    instanceid = jContainer.closest("div[instanceid]").attr("instanceid"),
                    instanceTimer,
                    durationIndex = 0;

                //初始化
                this.init = function () {

                    //拍卖专场的倒计时定时器都放在这里面
                    if(!jshop.paimaiCountDown){
                        jshop.paimaiCountDown = {};
                    }

                    instanceTimer = jshop.paimaiCountDown[instanceid];
                    if(!instanceTimer){
                        instanceTimer = [];
                    }

                    //清除掉此模块下原先对应的timer
                    for(var i = 0; i < instanceTimer.length; i++){
                        clearTimeout(instanceTimer[i]);
                    }

                    //合并参数
                    $.extend(setting, param);

                    jDay = jContainer.find(setting.dayCnt);
                    jHour = jContainer.find(setting.hourCnt);
                    jMinu = jContainer.find(setting.minuteCnt);
                    jSeco = jContainer.find(setting.secondCnt);
                    jTime = jContainer.find(setting.timerContainer);

                    endTime = new Date(duration[durationIndex].countDownTime.replace(/-/g, "/").replace(/\.\d/, "")).getTime();
                    jContainer.addClass(duration[durationIndex]["class"]);

                    //确定更新时间的周期
                    setting.secondCnt && (interval = 1000);
                    run();
                }


                //更新并显示倒计时
                //@param residualTime
                //@return {number}    剩余的秒数；
                //@private
                function updateTime(residualTime) {
                    nowTime += 1000;
                    var secondUnit = 1000,
                        minuteUnit = secondUnit * 60,
                        hourUnit = minuteUnit * 60,
                        dayUnit = hourUnit * 24,
                        day = 0;

                    if (setting.hasDay) {
                        day = ~~(residualTime / dayUnit);
                        jDay.text(day);
                        var hour = ~~((residualTime - day * dayUnit) / hourUnit);
                    } else {
                        var hour = ~~(residualTime / hourUnit);
                    }
                    jHour.text(hour < 10 ? "0" + hour : hour);

                    var minute = ~~((residualTime - day * dayUnit - hour * hourUnit) / minuteUnit);
                    jMinu.text(minute < 10 ? "0" + minute : minute);
                    var second = ~~((residualTime - day * dayUnit - hour * hourUnit - minute * minuteUnit) / secondUnit);
                    jSeco.text(second < 10 ? "0" + second : second);
                    return second;
                }

                function run() {

                    //隐藏天
                    setting.hasDay === false && jDay.hide() && jDay.next().hide();

                    //获取系统时间是个异步方法，所以后面的执行逻辑都需要放到回调方法中
                    getServerTime(function (date) {
                        nowTime = beginTime = new Date(date).getTime();
                        var residual = endTime - beginTime;

                        function one() {
                            updateTime(residual);
                            residual -= interval;
                            //如果天倒计时已经为0了，则隐藏天
                            setting.hasDay && ~~jDay.text() === 0 && jDay.hide() && jDay.next().hide();
                            //结束了就清掉timer
                            if (residual <= 0 && jSeco && ~~jSeco.text() === 0) {
                                clearTimeout(instanceTimer[timerIndex]);
                                var nextDuration = duration[durationIndex + 1];
                                if(nextDuration){
                                    jContainer.removeClass(duration[durationIndex]["class"]).addClass(nextDuration["class"]);
                                    //如果还有下一个倒计时，则启动下一个倒计时
                                    if(nextDuration.countDownTime){
                                        residual = new Date(duration[++durationIndex].countDownTime.replace(/-/g, "/").replace(/\.\d/, "")).getTime() - nowTime;
                                        instanceTimer[timerIndex] = setInterval(one, interval);
                                    }
                                }
                            }
                        }

                        if (residual > 0) {
                            instanceTimer[timerIndex] = setInterval(one, interval);
                            one();
                        } else {
                            jDay.text("00");
                            jHour.text("00");
                            jMinu.text("00");
                            jSeco.text("00");
                        }

                        jTime.css("visibility", "visible");

                    });
                }

            }

            !function init(){
                var ids = jTarget.find(param.idNode).map(function(){return this.getAttribute("albumId")}).toArray().join(",");
                getInfo(ids);
            }();
        }

tab:function(args){
        var param = $.extend({tabNode:'.jSortTab span', arrow:'.jSortTabArrow', defaultClass:'current', tabContent:'.jSortContent ul',isNeedWidth:true, eventType:'mouseenter'}, args),
            _this = this,
            tabNode = $(_this).find(param.tabNode),
            tabContent = $(_this).find(param.tabContent),
            arrow = $(_this).find(param.arrow), index = 0;

        var eventFlag = true;

        //初始化结构
        tabNode.eq(0).addClass(param.defaultClass);
         tabContent.eq(0).addClass(param.defaultClass).data('lazyload',true);

        var width = (tabNode.parent().parent().width()-0.03)/tabNode.length;
        if(param.isNeedWidth){
            tabNode.css({width: width});
        }
        arrow.css({width: width});

        //绑定鼠标移动事件
        tabNode.each(function(ind,n){
            $(n)[param.eventType](function(){
                index = ind;
                if(eventFlag){
                    eventFlag = false;
                    $(this).addClass(param.defaultClass).siblings().removeClass(param.defaultClass);
                    tabContent.eq(index).addClass(param.defaultClass).siblings().removeClass(param.defaultClass);
                    if(arrow.length){
                        arrow.animate({
                            left: (index)width
                        },
                        300,
                        function() {
                            eventFlag=true;
                            if(index != ind){
                                tabNode.eq(index).trigger(param.eventType);
                            }
                        });
                    }
                    else{
                        eventFlag = true;
                        if(index != ind){
                            tabNode.eq(index).trigger(param.eventType);
                        }
                    }
                }
                if(!tabContent.eq(index).data('lazyload')){
                    jshop.module.ridLazy(tabContent.eq(index).data('lazyload',true));
                }
            });
        });
    }

