base : function(arg){
			var _this = $(this),
				_args = $.extend({
					horizontal : 1,
					top : 0,
					show : false,
					animate : 'normal'
				},arg || {}),
			_slide = _this.find('.J-container'),
			_h = _slide.height(),
			_scroll_h = 609,
			_show = false,
			_duration = 1000,
			_original_top = null,
			_animate;

			var _show_animate = {
				normal : {
					show : function(){
						_slide.show();
					},
					hide : function(){
						_slide.hide();
					}
				},
				fade : {
					show : function(){
						_slide.fadeIn(_duration);
					},
					hide : function(){
						_slide.fadeOut(_duration);
					}
				},
				fadeH : {
					show : function(){
						_slide.animate({'height':_args.height + 'px'},_duration);					
					},
					hide : function(){
						_slide.animate({'height':'0px'},_duration,function(){
							_slide.show();
						});

					}
				},
				fadeW : {
					show : function(){
						_slide.animate({'width':_args.width + 'px'},_duration);
					},
					hide : function(){
						_slide.animate({'width':'0px'},_duration,function(){
							_slide.show();
						});
					}
				}
			};
			function _init(){
				if(_this.parents('[instanceid]').data('panel'))
					_this.parents('[instanceid]').data('panel').remove();
				_this.parents('[instanceid]').data('panel',_slide); 
				_animate = _show_animate[_args.animate] || _show_animate.normal;
				jshop.module.ridLazy(_this);
				_css_init();
			}

			function _css_init(){
				if(1 == _args.horizontal){
					_slide.css({
						right : '50%',
						marginRight : '505px'
					});
				}
				else if(2 == _args.horizontal){
					_slide.css({
						left : '50%',
						marginLeft : '505px'
					});
				}
				else{
					_slide.css({
						margin : '0 auto',
						maxWidth : '990px',
						left : '50%',
						marginLeft : -_args.width/2 + 'px'
					});
				}
				_original_top = _args.horizontal < 3?_args.top:(_args.horizontal == 4?2:$(window).height() - _args.height - 2);					
				_slide.css({
					position : 'fixed',
					zIndex : 7,
					top : _original_top + 'px',
					overflow : 'hidden'
				});
				if(_args.height){
					_slide.height(_args.height);
				}
				if(_args.width){
					_slide.width(_args.width);
				}

				if(_args.show === 1){
					if($(window).scrollTop() < _scroll_h){
						_animate.hide();
					}
					else{
						if(_args.horizontal == 4){
							if($(window).scrollTop() >= 176)
								_slide.show();
								_show = true;
						}
						else{
							_slide.show();
							_show = true;
						}
					}
					_scroll_handle();
				}
				else{
					if(_args.horizontal == 4){
						_animate.hide();
						if($(window).scrollTop() >= 175){
							_animate.show();
							_show = true;
						}
						$(window).scroll(function(){
							if(!_show && $(this).scrollTop() >= 176){
								_animate.show();
								_show = true;
							}
							if(_show && $(this).scrollTop() < 176){
								_animate.hide();
								_show = false;
							}
						});
					}
					else{
						_animate.hide();
						_animate.show();
						_show = true;
					}
				}
				if($.browser.msie&&$.browser.version.match(/6/)){
					_slide.css('position','absolute');
					_ie_fix_handle();
				}
			}
			function _ie_fix_handle(){
			//var __vertical = ($('#service-2013').length?$('#service-2013').offset().top:$('body').height()) - _args.height - _args.top;
				$(window).scroll(function(){
					//if(_args.horizontal > 2){
						if(_show){
							_slide.css('top',_original_top + $(this).scrollTop() + 'px');
						}
				});
				$(window).resize(function(){
					_original_top = _args.horizontal < 3?_args.top:(_args.horizontal == 4?2:$(window).height() - _args.height - 2);	
					if(_show){
						_slide.css('top',_original_top + $(this).scrollTop() + 'px');
					}
					return;
				});
			}

			function _scroll_handle(){
				$(window).scroll(function(){
					if(!_show && $(this).scrollTop() >= _scroll_h){
						_animate.show();
						_show = true;
					}
					if(_show && $(this).scrollTop() < _scroll_h){
						_animate.hide();
						_show = false;
					}
				});
			}

			function _bottom_scroll_handle(){
				var __vertical = ($('#service-2013').length?$('#service-2013').offset().top:$('body').height()) - _args.height - _args.top;
				$(window).scroll(function(){
					if($(this).scrollTop() >= __vertical){
						_slide.css({
							top : __vertical - $(this).scrollTop() + _args.top + 'px'
						});
					}
					else{
						_slide.css({
							top : _args.top + 'px'
						});
					}
				});
			}
			_init();
		}

autoLayout:function(args){
            if(args == undefined){
                if(validateData($(this).attr("module-param"))){
                    var args = eval('('+$(this).attr("module-param")+')');
                }
            }

            var param = $.extend({node:'li', extra:{}}, args),
                _this = this,
                elems = $(_this).find(param.node),
                elem = elems.eq(0);

            elem.css(param.extra);

            var outerWidth = parseInt(elem.data('outerWidth') || elem.outerWidth(true)),
                width = parseInt(elem.data('width') || elem.css('width')),
                qty = parseInt(elem.parent().parent().width()/outerWidth);

            //记录初始化值
            elem.data({'outerWidth':outerWidth, 'width':width});

            var extraWidth = outerWidth - width;
            var newWidth = (elem.parent().parent().width()-extraWidth*qty-0.03)/qty;
            elems.css({width:newWidth});
        }

