showComment: function(args) {
            var param = $.extend({
                parentContainer: '.JS_slide_item',
                childContainer: 'li',
                commentContainer:'.JS_summary_num',
                indexTag: '.JS_tag',
                goodsId: 'goods-id'
            }, args),
                _this = $(this),
                commentUrl = '//club.jd.com/clubservice/summary-m-';
            _this.find(param.commentContainer).each(function(){
                commentUrl += jQuery(this).attr(param.goodsId) + ',';
            });
            commentUrl += '.html';
            // 获取评价数渲染到已购买人数
            jQuery.ajax({
                url: commentUrl,
                dataType: 'jsonp',
                success: function(data) {
                    for (var i = data.CommentsCount.length - 1; i >= 0; i--) {
                        _this.find('[' + param.goodsId + '=' + data.CommentsCount[i].SkuId + ']').html(data.CommentsCount[i].CommentCount);
                    };
                    // 按已购买人数排序
                    _this.find(param.parentContainer).each(function(){
                        var $this = jQuery(this),
                            liArr = $this.find(param.childContainer);
                        liArr.sort(function(a, b){
                            return parseInt(jQuery(a).find(param.commentContainer).html()) > parseInt(jQuery(b).find(param.commentContainer).html()) ? -1 : 1;
                        }).each(function(n){
                            jQuery(this).find(param.indexTag).html(n+1);
                        });
                        $this.empty().append(liArr);
                    });
                }
            });
        }

