tabToggle: function(args) {
            var param = $.extend({
                tabContainer: '.jItem',
                tabNode:'.jSortTab a',
                tabContent:'.jSortContent ul'
            }, args),
            _this = this,
            tabContainer = $(_this).find(param.tabContainer);

            tabContainer.each(function(){
                var $this = $(this),
                    tabNode = $this.find(param.tabNode),
                    tabContent = $this.find(param.tabContent);
                // 初始化商品显示(默认显示第一个)
                $(tabNode[0]).addClass('current');
                $(tabContent[0]).addClass('current');

                tabNode.each(function(n){
                    var $this = $(this),
                        currentContent = $(tabContent[n]);
                    // 绑定商品链接
                    $this.attr('href', currentContent.find('.JGood').attr('href'));

                    // 绑定鼠标移到tab上的切换动作
                    $this.bind('mouseenter', function(){
                        // 商品展示
                        tabContent.removeClass('current');
                        currentContent.addClass('current');
                        // tab状态切换
                        tabNode.removeClass('current');
                        $this.addClass('current');
                    });
                });
            });
        }

timer :(function(){
			var _timer = null,
				_clear = function(){
					if(_timer)
						clearInterval(_timer);
				};
			return function(arg){
				var _this = this, 
				_ids = [],
				_sysTime = null,
				_allIds = [],
				_args = $.extend({isNeedZero:true},arg);
				__getIds = function(){
					$(_this).find('div[tuanid]').each(function(index,n){
						_ids.push($(n).attr('tuanid'));
					});
					$('div[tuanid]').each(function(index,n){
						_allIds.push($(n).attr('tuanid'));
					});
				},


				 __init = function(){
					__getIds();
					__initData();
					__initTimer();
				},

				__initData = function(){
					if(!_sysTime && !!_ids.length){
						var localT = new Date().getTime(),
							sysT = jQuery('.jItem[tuanId='+_ids[0]+']').attr('currentTime');
						_sysTime = localT - sysT;	
					}
					for(var i  = _ids.length; i > 0; i--){
						var item = jQuery('.jItem[tuanId='+_ids[i-1]+']'),
							current = parseInt(item.find('.jGoodsInfo>.jTotalQty>.jLimitQty>.jNum').html()),
							barCon = item.find('.jGoodsInfo>.jProgress>.jBar>.jIconBar'),
							total = parseInt(barCon.find('em').html());
						barCon.find('.jCurrent').css('width',(100-current*100/total)+'%');

						var endT = parseInt(item.attr('endTime'))*1000,
							periodT = Math.floor((endT - parseInt(item.attr('currentTime')))/1000);
						if(periodT < 0) {
							var str = '00:00:00';
						}
						else{
							var hour =Math.floor(periodT/3600),
								minute = Math.floor(periodT%3600/60),
								second = periodT%60;
							if(_args.isNeedZero){
								var str = hour <= 9 ? ('0' + hour) : hour;
								str += ':';
								str += minute <= 9 ? ('0' + minute) : minute; 
								str += ':';
								str += second <= 9 ? ('0' + second) : second;
							}
							else{
								var str = hour + ':' + minute + ':' + second;
							}
						}
						item.find('.jTime>.jNum').html(str); 
					}
				},

				__initTimer = function(){
					_clear();
					_timer = setInterval(function(){
						for(var i = _allIds.length; i > 0; i--){
							var item = jQuery('.jItem[tuanId='+_allIds[i-1]+']'),
								localT = new Date().getTime(),
								periodT = Math.floor((parseInt(item.attr('endTime'))*1000 - localT + _sysTime)/1000);
							if(periodT < 0){
								_allIds.splice(i-1,1);
								break;
							}
							var str = Math.floor(periodT/3600);
								str += ':' + Math.floor(periodT%3600/60);
								str += ':' + periodT%60;	
							item.find('.jTime>.jNum').html(str); 
						}
					},1000);
				};

				__init();
			}
		})()

//timer : {}, 
  base : function(arg){
			var _this = this,
			 	_args = $.extend({
				mainPic : '.main-pic',
				sidePic : '.side-pic',
				timeTag : '.jIconTime',
				video : '.video'
			},arg || {}),
			_timer = null,
			_duration = 500,
			_animating = false,
			_in_flag = false,
			_has_video = false,
			_side_w = $(_this).find(_args.sidePic).width();

			function _init(){
				_has_video = !!$(_this).find(_args.video).length;
				_event_init();
				_timer_handler();
			}

			function _event_init(){
				var __side = $(_this).find(_args.sidePic);
				if(!_has_video){
					$(_this).find(_args.mainPic).mouseover(function(){
						if(_animating) return;
						_in_flag = true;
						_animating = true;
						__side.animate({
							width : '0px'
						},_duration,function(){
							_animating = false;
							if(!_in_flag){
								__side.animate({
									width : _side_w + 'px'
								},_duration);
							}
						});
					}).mouseout(function(){
						_in_flag = false;
						if(!_animating){
							$(_this).find(_args.sidePic).animate({
								width : _side_w + 'px'
							},_duration);
						}
					});
				}
				else{
					$(_this).find(_args.mainPic).mouseover(function(){
						$(this).animate({height:'0px'},_duration);
						__side.animate({width : '0px'},_duration);
					});

					$(_this).find('.icon_close').click(function(){
						$(_this).find(_args.mainPic).animate({height:'445px'},_duration);
						__side.animate({width:'198px'},_duration,function(){
							var __html = $(_this).find(_args.video).html(),
							__parent = $(_this).find(_args.video).parent();
							$(_this).find(_args.video).remove();
							$('<div class="' + _args.video +'">' + __html + '</div>').prependTo(__parent);
						});
					});
				}
			}

			function _timer_handler(){
				var __sku = $(_this).find('[skuid]').attr('skuid');
				if(w.jshop.module.videoPromo[__sku]){
					clearInterval(w.jshop.module.videoPromo[__sku]);
				}
				var __container = $(_this).find(_args.timeTag),
					__seconds = Math.floor(parseInt(__container.attr('resttime'))/1000);
				w.jshop.module.videoPromo[__sku] = setInterval(function(){
					var __date = parseInt(__seconds/3600);
						__date +='时';
						__date += parseInt(__seconds%3600/60);
						__date += '分';
						__date += __seconds%60;
						__date += '秒';
						__seconds --;
						if(__seconds == 1){
							__seconds = 86400;
						}
						__container.html(__date);
				},1000);
			}

			_init();
		}

base : function(arg){
			var _this = $(this),_items,_id,_pin = '',_itemid,
				_url = INTERFACE.vote.vote,
				_arg = $.extend({
					faddingType : 1,
					dialogCls : '.voteResult',
					isLogin : true,
					position : 2,
					duration : 3
				},arg || {}),
				_timer = null,
				_duration = parseInt(_arg.duration) * 1000,
				_current_item = null,
				_dialog = _this.find(_arg.dialogCls),
				_close_flag = true;

			function _init(){
				_get_vote_count(_get_ids());
				_event_register();
			}

			function _get_ids(){
				var __ids = [];
				_items =  _this.find('[itemid]');
				_items.each(function(index,n){
					__ids.push($(n).attr('itemid'));
				});
				return __ids.join(',');
			}

			function _vote(){
				var __url = _url ;
				$.ajax({
					url : __url,
					data : {
						id : _id,
						pin : _pin,
						itemId : _itemid
					},
					cache : false,
					dataType : 'jsonp',
					success : function(content){
						if(!content) return;
						if(content.isSuccess){
							$('[votecount='+content.itemId+']').html(content.count);
						}
						if(!_dialog.length){
							_close_flag = true;
						}
						else{
							_fill_cnt(content);
						}
					}
				});
			}

			function _get_vote_count(ids){
				var __url = INTERFACE.vote.getCount;
				$.ajax({
					url : __url,
					data : {ids : ids},
					dataType : 'jsonp',
					success : function(data){
						for(var i in data){
							var node = $('[votecount='+i+']');
							node.each(function(index,n){
								$(n).html(data[i]);
							});
						}
					}
				});
			}

			function _fill_cnt(cnt){
				if(!_arg.selfMessage){
					if(cnt.isSuccess){
						_dialog.find('.success').show();
						_dialog.find('.fail').hide();
					}
					else{
						_dialog.find('.success').hide();
						_dialog.find('.fail').show();
						_dialog.find('.fail .specific').html(cnt.tip);
					}
				}
				else{
					_dialog.removeClass('jMessageOk,jMessageError').addClass(cnt.isSuccess?'jMessageOk':'jMessageError');
					_dialog.html(cnt.tip);
				}
				_show_result();
			}

			function _show_result(){
				if(!_arg.selfMessage){
					var __left = (_arg.position == 1?(_current_item.offset().left + parseInt(_current_item.outerWidth(true)/2)):(parseInt($(window).width()/2))) - _dialog.outerWidth()/2 + 'px',
						__top = (_arg.position == 1?(_current_item.offset().top + parseInt(_current_item.outerHeight(true)/2)):(parseInt($(window).height()/2) + $(window).scrollTop())) - _dialog.outerHeight()/2 + 'px';
					_dialog.css({
						position : 'absolute',
						left : __left,
						top : __top
					});
				}
				_close_flag = false;
				_dialog.unbind('closed').bind('closed',function(){
					_close_flag = true;
				});
				_dialog.fadeIn();
				if(_arg.faddingType == 1){
					_timer = setTimeout(function(){
						_dialog.fadeOut();
						_close_flag = true;
					},_duration);
				}
			}

			function _event_register(){
				_items.each(function(index,n){
					$(n).click(function(){
						//弹出结果关闭之前点击无效果
							if(!_close_flag)
								return;
							_current_item = $(this).closest('li').eq(0);
							_id = $(this).attr('voteid');
							_itemid = $(this).attr('itemid');
							if(_arg.selfMessage)
								_dialog = _current_item.find('.message');
						if(_arg.isLogin){
							thick_login(function(c){
								_pin = c.Name;
								 _vote();
							});
						}
						else{
							_vote();
						}
					});
				});
			}
			_init();
		}

